    ed_pjt.SetFocus();

	cal_zzshipf.value = substr(gfn_getCurrDate(),0,6)+"01";;  //substr(gfn_getCurrDate(),0,6);
    cal_zzshipt.value   = gfn_getCurrDate();  //substr(gfn_getCurrDate(),0,6);

   /* if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";		
	}
   */
   // if(F_ADMIN_YN = "Y") //관리부서 승인버튼활성
    if(G_USER_ID == '2022702' || G_USER_ID == '2021765' || G_USER_ID == '2026490'|| G_USER_ID == '2024357' || G_USER_ID == '2028891')
    {
        btn_approval.Enable  =true; 
        F_ADMIN_YN = "Y";
    }
    else
    {
        btn_approval.Enable  =false;
        F_ADMIN_YN = "N";
    }

    if(G_USER_ID == '2029090' || G_USER_ID == '2022702' || G_USER_ID == '2021765' || G_USER_ID == '2026490' || G_USER_ID == '2028891') //요청 단계 까지  관리자(영업 조경주 SW) 삭제 가능
    {
      F_DEL = "Y";
    }
   else
   {
      F_DEL = "N";
   }

     btn_add.Enable = false;
     btn_del.Enable = false;

    cb_state.Index = 0;
   	cb_period.Index = 0;
}

