/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
// 파일 업로드 
  if ( nCell == 16 ) {

     if(ds_list.getColumn(nRow, "STATE") == 'I' || ds_list.getColumn(nRow, "STATE") == 'N')
     {
        imgUploadFlg = "";
     }
     else
     {
       imgUploadFlg = "DOWN";
     }
//	ALERT("A");
		var arr = gfn_openFileMgr(
		    "PS"
			, ds_list.getColumn(nRow, "FNAME")//FileDialog.FileName
			, ds_list.getColumn(nRow, "FNAME_PATH")//FileDialog.FilePath
			, imgUploadFlg
			, ""
			, ""
			, ""
			, "true"
		);

		if ( arr != null ) {
		   ds_list.setColumn(nRow, "FNAME_PATH",arr[0]) ; // 디렉토리 정보 
		   ds_list.setColumn(nRow, "FNAME",arr[1]); // 파일명 
		}

		//alert( ds_list.GetColumn(nRow, "FNAME_PATH"));

		//alert( ds_list.GetColumn(nRow, "FNAME"));
	    //alert(arr);
  }
  else{

  	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;


	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;

	if(nCell == 12)	{
		cal_temp.value = ds_list.getColumn(nRow, "BLDAT");	
	}   
	 cal_temp.SetFocus();
   }
}
