/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

  var fileName = "";

  if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
	if(fn_checkSave())
	{
    //필수체크 
      for(var i=0; i<ds_list.GetRowCount(); i++)
	  {    
	       if(length(ds_list.getColumn(i, "FNAME")) != 0 && length(ds_list.getColumn(i, "FNAME_PATH")) != 0)
	       {
	           	fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list.getColumn(i, "FNAME");
				var arr = tit_fileWrite(httpFile
					, "PS"
					, ds_list.getColumn(i, "FNAME_PATH")
					, ds_list.getColumn(i, "FNAME")
					, fileName// 실제 db에 저장될 파일 이름
					, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
					, "");    // ds_list.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명 
				if ( arr[0] == "S" ) {
					ds_list.setColumn(i, "FNAME_APP", arr[2]);
					ds_list.setColumn(i, "FPATH", ds_list.getColumn(i, "FNAME_PATH"));
				}
				else if ( arr[0] != "S" )
				{
					gfn_showMsg("CW00035"); 
					return;
				}

	      }

	     //   alert( ds_list.GetColumn(i,"ITEMTX"));

	}

    ds_list.Filter("CHK= 1 && length(FNAME) > 0");
    ds_list_i.CopyF(ds_list);
    ds_list.UnFilter();


    ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

/* ZPST0117 저장 */

    tit_clearActionInfo();
	tit_addSelectExActionInfo("ps04:PS0410003A_S5", "ps04:PS0410003A_I1", "ps04:PS0410003A_U1");
	tit_addSelectExActionInfo("ps04:PS0410003A_S6", "ps04:PS0410003A_I3", "ps04:PS0410003A_U4");// zpst0117s
	tit_callService(
		  ""	
		, ""
		, "ds_list=ds_list2 ds_list_i=ds_list_i "
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""
    );


/* ZPST0117L 저장 */


    tit_clearActionInfo();	
	//tit_addSelectExActionInfo("ps04:PS0410002A_S13", "ps04:PS0410002A_I2", "ps04:PS0410002A_U1");
	tit_addMultiActionInfo("ps04:PS0410003A_D1");
	tit_addMultiActionInfo("ps04:PS0410003A_I2");
	tit_callService(
        ""
        , ""        
        , "ds_list=ds_list2"
        , ""
        , ""
        , "fn_afterSave2"
		, ""
		, ""
        ,true  // ""
        );	

  }

}
/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave(){

//alert("a");
   //필수체크 
 for(var i=0; i<ds_list.GetRowCount(); i++)
 { 
   if(ds_list.GetColumn(i, "CHK")==1){
	     //   alert(ds_list.GetColumn(i,"STATE"));
	       if(ds_list.GetColumn(i,"STATE") == 'A' || ds_list.GetColumn(i,"STATE") == 'B'|| ds_list.GetColumn(i,"STATE") == '3') // 저장의 경우 최초입력/입력상태
		   {
			  gfn_showMsg("CW10025","요청/승인/완료의 경우 수정이 불가합니다.상태값을 확인해주세요.");
			  return;
		   }	
	       // 최초 입력 시 필수값 체크(금액/vat/발행일/지급업체/작업내역)
	       if(ds_list.GetColumn(i,"STATE") == 'I' && length(trim(ds_list.GetColumn(i,"WRKLFN"))) <=  0)
	       {
			  gfn_showMsg("CW00001","지급업체");
			  return;
		   }	
	       if(ds_list.GetColumn(i,"STATE") == 'I' && length(trim(ds_list.GetColumn(i,"TWBTR"))) <=  0)
	       {
			  gfn_showMsg("CW00001","금액");
			  return;
		   }		   
		   if(ds_list.GetColumn(i,"STATE") == 'I' && length(trim(ds_list.GetColumn(i,"BLDAT"))) <=  0)
	       {
			  gfn_showMsg("CW00001","계산서발행일");
			  return;
		   }
		   if(ds_list.GetColumn(i,"STATE") == 'I' && length(trim(ds_list.GetColumn(i,"ITEMCD"))) <=  0)
	       {
			  gfn_showMsg("CW00001","작업내역");
			  return;
		   }
		   if(ds_list.GetColumn(i,"WASTE_CK2") == '')
	       {
			  gfn_showMsg("CW00001","구분");
			  return;
		   }	
		   // 입력상태의 경우 첨부서류 필수
		    if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"FNAME"))) <=  0)
	       {
			  gfn_showMsg("CW00001","첨부서류");
			  return;
		   }
		   // 업체 10자리 체크 
		    if(length(trim(ds_list.GetColumn(i,"WRKLFN"))) <> 10)
	       {
			  gfn_showMsg("CW10025","지정업체 사업자번호 10자리를 확인해주세요.");
			  return;
		   }
		    if(length(trim(ds_list.GetColumn(i,"MWBTR"))) <=  0)
	       { 
	         ds_list.SetColumn(i, "MWBTR" , 0);
		   }
		   //alert(ds_list.GetColumn(i,"WASTE_CK2"));

		   	if(ds_list.GetColumn(i,"WASTE_CK2") == 'C')
	       {  
			   ds_list.SetColumn(i,"WASTE_CK", "");
		   }
		   else
		   {
		       ds_list.SetColumn(i,"WASTE_CK", ds_list.GetColumn(i,"WASTE_CK2"));
		   }

		    if(ds_chk.GetRowCount()==0)	ds_chk.AddRow();

		    ds_chk.SetColumn(0,"PSPID",ds_list.GetColumn(i,"PSPID")); 
		    ds_chk.SetColumn(0,"BLDAT",ds_list.GetColumn(i,"BLDAT")); 
		    ds_chk.SetColumn(0,"WRKLFN",ds_list.GetColumn(i,"WRKLFN")); 
		    ds_chk.SetColumn(0,"TWBTR",ds_list.GetColumn(i,"TWBTR") / 100); 
		    ds_chk.SetColumn(0,"MWBTR", ds_list.GetColumn(i,"MWBTR") / 100) ;

		        tit_clearActionInfo();
		        tit_addSearchActionInfo("ps04:PS0410003A_S7");
		        tit_callService(
			     ""
			   , ""
			   , "ds_chk=ds_chk"
			   , "ds_list_c=ds_list_c"
			   , ""
			   , ""//"fn_chk"
			   , ""
               , ""
			   , true);	


			//alert(ds_list_c.GetColumn(0,"CHK"));
             if(ds_list_c.GetColumn(0,"CHK") >= 1)
             { // alert(ds_list_c.GetColumn(0,"CHK"));
                gfn_showAlert("CW10025","이미 등록된 계산서 입니다. 확인바랍니다.");
	            return;
             }

	        ds_list.SetColumn(i,"ITEMTX",ds_item.GetColumn(ds_item.FindRow("CODE", ds_list.GetColumn(i, "ITEMCD")), "CODE_NAME"));  //항목에 text 저장 
	     }
	 }

   	return true;

}
