function grd_list_OnKillFocus(obj)
{
	ds_list.SetColumn(ds_list.currow,"NETWR", ds_list.GetColumn(ds_list.currow, "MWBTR") + ds_list.GetColumn(ds_list.currow, "TWBTR"))  ;  //단가 
}

function grd_list_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{   
    var arr = 0;
    var arr2 = 0;

	 if(nCell == 9 ) 
	{
	   arr = ds_list.GetColumn(nRow, "TWBTR") / 10 ;  
       arr2 = ds_list.GetColumn(nRow, "TWBTR") ;
	   ds_list.SetColumn(nRow, "MWBTR" , arr );
	   ds_list.SetColumn(nRow, "NETWR" , arr + arr2 );
	}
	else if(nCell == 10 )
	{
      ds_list.SetColumn(nRow, "NETWR" ,  ds_list.GetColumn(nRow, "MWBTR") + ds_list.GetColumn(nRow, "TWBTR") );
	}
}

/********************************************************************************
* 기      능   : 접수 
********************************************************************************/
function fn_receipt() {

  if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

    //필수체크 
    for(var i=0; i<ds_list.GetRowCount(); i++)
	{
	    if(ds_list.GetColumn(i, "CHK")==1){
	     //   alert(ds_list.GetColumn(i,"STATE"));
	       if(ds_list.GetColumn(i,"STATE") == 'I' ||ds_list.GetColumn(i,"STATE") == 'A' || ds_list.GetColumn(i,"STATE") == 'B'|| ds_list.GetColumn(i,"STATE") == '3') // 저장의 경우 최초입력/입력상태
		   {
			  gfn_showMsg("CW10025","요청/승인/완료의 경우 접수가 불가합니다.상태값을 확인해주세요.");
			  return;
		   }	
	       // 최초 입력 시 필수값 체크(금액/vat/발행일/지급업체/작업내역)
	       if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"WRKLFN"))) <=  0)
	       {
			  gfn_showMsg("CW00001","지급업체");
			  return;
		   }	
	       if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"TWBTR"))) <=  0)
	       {
			  gfn_showMsg("CW00001","금액");
			  return;
		   }
		   /*if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"MWBTR"))) <=  0)
	       {
			  gfn_showMsg("CW00001","VAT");
			  return;
		   }
		   */
		   if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"BLDAT"))) <=  0)
	       {
			  gfn_showMsg("CW00001","계산서발행일");
			  return;
		   }
		   if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"ITEMCD"))) <=  0)
	       {
			  gfn_showMsg("CW00001","작업내역");
			  return;
		   }
		   // 입력상태의 경우 첨부서류 필수
		    if(ds_list.GetColumn(i,"STATE") == 'N' && length(trim(ds_list.GetColumn(i,"FNAME"))) <=  0)
	       {
			   gfn_showMsg("CW00001","첨부서류");
			   return;
		   }
		   // 업체 10자리 체크 
		     if(length(trim(ds_list.GetColumn(i,"WRKLFN"))) <> 10)
	       {
			  gfn_showMsg("CW10025","지정업체 사업자번호 10자리를 확인해주세요.");
			  return;
		   }
		   if(length(trim(ds_list.GetColumn(i,"MWBTR"))) <=  0)
	       { 
	         ds_list.SetColumn(i, "MWBTR" , 0);
		   }
		 	if(ds_list.GetColumn(i,"WASTE_CK2") == 'C')
	       {  
			   ds_list.SetColumn(i,"WASTE_CK", "");
		   }
		   else
		   {
		       ds_list.SetColumn(i,"WASTE_CK", ds_list.GetColumn(i,"WASTE_CK2"));
		   }

	        ds_list.SetColumn(i,"ITEMTX",ds_item.GetColumn(ds_item.FindRow("CODE", ds_list.GetColumn(i, "ITEMCD")), "CODE_NAME"));  //항목에 text 저장 

	       if(length(ds_list.getColumn(i, "FNAME")) != 0 && length(ds_list.getColumn(i, "FNAME_PATH")) != 0)
	       {
	           	fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list.getColumn(i, "FNAME");
				var arr = tit_fileWrite(httpFile
					, "PS"
					, ds_list.getColumn(i, "FNAME_PATH")
					, ds_list.getColumn(i, "FNAME")
					, fileName// 실제 db에 저장될 파일 이름
					, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
					, "");    // ds_list.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명 
				if ( arr[0] == "S" ) {
					ds_list.setColumn(i, "FNAME_APP", arr[2]);
					ds_list.setColumn(i, "FPATH", ds_list.getColumn(i, "FNAME_PATH"));
				}
				else if ( arr[0] != "S" )
				{
					gfn_showMsg("CW00035"); 
					return;
				}

	       }
	     }

	     //   alert( ds_list.GetColumn(i,"ITEMTX"));

	}


	ds_list.Filter("CHK= 1 && length(FNAME) > 0");
    ds_list_i.CopyF(ds_list);
    ds_list.UnFilter();


    ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

/* ZPST0117 저장 */

    tit_clearActionInfo();
	tit_addSelectExActionInfo("ps04:PS0410003A_S5",, "ps04:PS0410003A_U2");
	tit_addSelectExActionInfo("ps04:PS0410003A_S6", "ps04:PS0410003A_I3", "ps04:PS0410003A_U4");// zpst0117s
	tit_callService(
		  ""	
		, ""
		, "ds_list=ds_list2 ds_list_i=ds_list_i "
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""
    );


/* ZPST0117L 저장 */


    tit_clearActionInfo();	
	//tit_addSelectExActionInfo("ps04:PS0410002A_S13", "ps04:PS0410002A_I2", "ps04:PS0410002A_U1");
	tit_addMultiActionInfo("ps04:PS0410003A_D1");
	tit_addMultiActionInfo("ps04:PS0410003A_I2");
	tit_callService(
        ""
        , ""        
        , "ds_list=ds_list2"
        , ""
        , ""
        , "fn_afterSave2"
		, ""
		, ""
        ,true  // ""
        );		

}

/********************************************************************************
* 기      능   : 승인
********************************************************************************/
function fn_approval() {

  if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


    for(var i=0; i<ds_list.GetRowCount(); i++)
	{  
	  if(ds_list.GetColumn(i, "CHK")==1){

	   if(ds_list.GetColumn(i,"STATE") <> 'A') // 저장의 경우 최초입력/입력상태
		   {
			  gfn_showMsg("CW10025","입력/접수/완료의 경우 승인이 불가합니다.상태값을 확인해주세요.");
			  return;
		   }
		 //업체등록확인
		  if(ds_list.GetColumn(i,"LIFNR_NM") == '')
	       {
			  gfn_showMsg("CW10025","등록된 업체가 아닙니다. 업체등록을 진행해주시기 바랍니다. ");
			  return;
		   }
 	     if(ds_list.GetColumn(i,"WASTE_CK2") == 'C')
	       {  
			   ds_list.SetColumn(i,"WASTE_CK", "");
		   }
		   else
		   {
		       ds_list.SetColumn(i,"WASTE_CK", ds_list.GetColumn(i,"WASTE_CK2"));
		   }
		// 업체등록(정발행/역발행확인)
		/*   if(ds_list.GetColumn(i,"TAX") == 'X')
	       {
			  gfn_showMsg("CW10025","등록된 업체가 아닙니다. 업체등록을 진행해주시기 바랍니다. ");
			  return;
		   }
		   */
		   //부가세여부체크 
		   if(trim(ds_list.GetColumn(i,"MWBTR")) == 0)
	       {
			  ds_list.SetColumn(i,"MWNCHK" , 'X');
		   }
		   else
		   {
		      ds_list.SetColumn(i,"MWNCHK" , '');
		   }

		}

	}


	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

/* ZPST0117 저장 */

    tit_clearActionInfo();
	tit_addSelectExActionInfo("ps04:PS0410003A_S5",, "ps04:PS0410003A_U3");
	tit_callService(
		  ""	
		, ""
		, "ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterSave2"
		, ""
		, ""
		, true  // ""
    );


}


/********************************************************************************
* 기      능   : 삭제  
********************************************************************************/
function fn_delete() 
{

   if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


    for(var i=0; i<ds_list.GetRowCount(); i++)
	{
      if(ds_list.GetColumn(i, "CHK")==1){

	    if( ds_list.GetColumn(i,"STATE") == '1'|| ds_list.GetColumn(i,"STATE") == '2'|| ds_list.GetColumn(i,"STATE") == '3') {
	 		  gfn_showMsg("CW10025","요청/승인/완료의 경우 삭제가 불가합니다.");
			  return;
		  }

		if(ds_list.GetColumn(i,"STATE") == 'A' && F_DEL == 'N') {
		      gfn_showMsg("CW10025","요청/승인/완료의 경우 삭제가 불가합니다.");
			  return;
		   }
      }
     }

 	ds_list.Filter("CHK= 1 && length(FNAME) > 0");
    ds_list_d.CopyF(ds_list);
    ds_list.UnFilter();

	ds_list.Filter("CHK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

    /* ZPST0117 삭제  */

    tit_clearActionInfo();
	tit_addSelectExActionInfo("ps04:PS0410003A_S5",, "ps04:PS0410003A_D2");
	tit_callService(
		  ""	
		, ""
		, "ds_list=ds_list2"
		, ""
		, ""
		, ""
		, ""
		, ""
		, true  // ""
    );


/* ZPST0117L 삭제  */


    tit_clearActionInfo();	
	tit_addMultiActionInfo("ps04:PS0410003A_D1");
    if(ds_list_d.rowcount() > 0 )
    {
	 tit_addMultiActionInfo("ps04:PS0410003A_D3"); // ZPST0117S
	}
	tit_callService(
        ""
        , ""        
        , "ds_list=ds_list2 ds_list_d=ds_list_d"
        , ""
        , ""
        , "fn_afterDelete"
		, ""
		, ""
        ,true  // ""
        );		

}
/********************************************************************************
* 기      능   : 삭제  Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		//fn_query();
     for(var i=0; i<ds_list_d.GetRowCount(); i++)
	 {
	    tit_fileDelete("PS", ds_list_d.GetColumn(i, "FNAME_APP"), true);		
	 }

	  ds_list_d.DeleteAll();
	  fn_query();
  }
}
