/********************************************************************************
* 기      능   : 조회(프로젝트 정보)
********************************************************************************/
function fn_query1() 
{
	applyData();
	var nRow = 0;

	// 필수 입력사항 및 일자 체크
	if (ed_pspid.Text == '')
	{ 	
		var stcDate = "";
		nRow = ds_period.SearchRow("CODE == '"+cb_period.value+"'");

		if(nRow >=0 )
		{
			stcDate = ds_period.GetColumn(nRow, "CODE_NAME");
		}

		if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, stcDate) )
		{   

			return;
		}
		else
		{

			// 프로젝트 번호가 비어있을 경우, [신청일자 필수] ( 신청일자는 1년 기준 )
			if((cal_datef.Value == '' || length(cal_datef.Value) == 0) || (cal_datet.Value == '' || length(cal_datet.Value) == 0))
			{
				//gfn_openMsgWin("프로젝트번호가 공백일 경우"+
				//               "\n"+stcDate + "는 필수 입력사항입니다.");

				gfn_showMsg("프로젝트번호가 공백일 경우"+
				               " "+stcDate + "는 필수 입력사항입니다.");
				return;
			}
			else
			{
				gfn_getDayCnt(ds_dayCnt, cal_datef.Value, cal_datet.Value);
				nDays = ds_dayCnt.GetColumn(0, "DAYS");

				if(nDays > 365)
				{
					gfn_showMsg("프로젝트 미입력 시 조회 범위는 1년이내로 제한됩니다.");
					cal_datef.Value = gfn_getAfterDate(gfn_getCurrDate(),-365);
					cal_datet.Value = gfn_getCurrDate();
					return;
				}
			}
		}

		btn_add.Enable = false;
		btn_del.Enable = false;

	}

	//프로젝트 번호가 있어야지 추가가 가능함
	else 
	{
		if( ds_gbn.getColumn(0, "PSWEB_GBN") == 'A' || ds_gbn.getColumn(0, "PSWEB_GBN") == 'B' )
		{

		}
		else
		{
			btn_add.Enable = true;
			btn_del.Enable = true;
		}
    }



	// 조회 처리
	//if( ds_gbn.getColumn(0, "PSWEB_GBN") == '' )
	if( length(ds_gbn.getColumn(0, "PSWEB_GBN")) == 0 )
	{
		ds_cond.SetColumn(0,"PSWEB_GBN",'Y');
	}
	else
	{
		ds_cond.SetColumn(0,"PSWEB_GBN",'X');
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0420001A_C4"); 
	//tit_addSearchActionInfo("ps04:PS0420001A_C3"); 
	if(cb_period.Value == 'B')
	{
	    //tit_addSearchActionInfo("ps04:PS0420001A_C3");  
		tit_addSearchActionInfo("ps04:PS0420001A_S1");
    } else{
        //tit_addSearchActionInfo("ps04:PS0420001A_C6"); 
        tit_addSearchActionInfo("ps04:PS0420001A_S5");
    }
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);

		/*
	obj.fireevent = false;
	for( var i=0; i<ds_list.rowcount; i++ )
	{			
		alert("i == "+i);
		alert("LIFNR_AF == "+ds_list.GetColumn(i,"LIFNR_AF"));
		fn_changeZztemno_grd_cd(ds_list.GetColumn(i,"LIFNR_AF"));

	}
	obj.fireevent = true;
		*/

}


