		ds_list.DeleteRow(ds_list.row);
	}
	else
	{

	}

}


/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
 if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}



/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 호기를 변경시 데이타 읽어옮
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{


//ds_posid.getColumn(ds_list.currow,"PSPID","");
//dsObj.getColumn(nRow, "O_TEXT");

	//alert("strColumnID=="+strColumnID);
	//alert("varOldValue=="+varOldValue);
	//alert("varNewValue=="+varOldValue);
	//alert("nPivotIndex=="+varOldValue);

	if ( strColumnID == 'POSID' )
	{
		ds_cond.SetColumn(0,"POSID",varNewValue);

		//alert(ds_cond.SaveXML());	

	    //gfn_initDs(ds_list_tmp, true);	

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps04:PS0420001A_S2");         
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_tmp=ds_list_tmp"
			, ""
			, //"fn_afterQuery"
			, ""
			, ""
			, true);		

		obj.fireevent = false;

		if(ds_list_tmp.getColumn(0, "CNT") <= 0){		
		//ds_list.SetColumn(ds_list.currow, "PSPID", ds_list_tmp.getColumn(0, "PSPID"));
		ds_list.SetColumn(ds_list.currow, "PSPID", left(varNewValue,6));
		//left(strText, 4)+"-"+substr(strText,4,2)+"-"+right(strText,2);
        ds_list.SetColumn(ds_list.currow, "POSID", ds_list_tmp.getColumn(0, "POSID"));

		ds_list.SetColumn(ds_list.currow, "ZSITE_NM", ds_list_tmp.getColumn(0, "ZSITE_NM"));
		ds_list.SetColumn(ds_list.currow, "LIFNR_BF", ds_list_tmp.getColumn(0, "LIFNR_BF"));
		ds_list.SetColumn(ds_list.currow, "LIFNR_AF", ds_list_tmp.getColumn(0, ""));

		ds_list.SetColumn(ds_list.currow, "TEMNO", ds_list_tmp.getColumn(0, "TEMNO"));

		ds_list.SetColumn(ds_list.currow, "PROPO_DAT", ds_list_tmp.getColumn(0, "PROPO_DAT"));
		ds_list.SetColumn(ds_list.currow, "PROPO_MAN", ds_list_tmp.getColumn(0, "PROPO_MAN"));

		ds_list.SetColumn(ds_list.currow, "SOGBN1", ds_list_tmp.getColumn(0, "SOGBN1"));
		ds_list.SetColumn(ds_list.currow, "SOGBN2", ds_list_tmp.getColumn(0, "SOGBN2"));	
		ds_list.SetColumn(ds_list.currow, "ZMAN", ds_list_tmp.getColumn(0, "ZMAN"));

		ds_list.SetColumn(ds_list.currow,"PROPO_DAT",gfn_getCurrDate());
		ds_list.SetColumn(ds_list.currow,"PROPO_MAN", G_USER_ID);

		ds_list.SetColumn(ds_list.currow, "CHK", ds_list_tmp.getColumn(0, "CHK"));
		ds_list.SetColumn(ds_list.currow, "GBN", ds_list_tmp.getColumn(0, "GBN"));
		ds_list.SetColumn(ds_list.currow, "SEQNO", ds_list_tmp.getColumn(0, "SEQNO"));
		ds_list.SetColumn(ds_list.currow, "FINAL_CHK", '');

	    }
	    else
	    {

	    ds_list.SetColumn(ds_list.currow, "PSPID", "");
		//left(strText, 4)+"-"+substr(strText,4,2)+"-"+right(strText,2);
        ds_list.SetColumn(ds_list.currow, "POSID", "");

		ds_list.SetColumn(ds_list.currow, "ZSITE_NM", "");
		ds_list.SetColumn(ds_list.currow, "LIFNR_BF","");
		ds_list.SetColumn(ds_list.currow, "LIFNR_AF","");

		ds_list.SetColumn(ds_list.currow, "TEMNO", "");

		ds_list.SetColumn(ds_list.currow, "PROPO_DAT","");
		ds_list.SetColumn(ds_list.currow, "PROPO_MAN", "");

		ds_list.SetColumn(ds_list.currow, "SOGBN1","");
		ds_list.SetColumn(ds_list.currow, "SOGBN2", "");	
		ds_list.SetColumn(ds_list.currow, "ZMAN","");

		ds_list.SetColumn(ds_list.currow,"PROPO_DAT","");
		ds_list.SetColumn(ds_list.currow,"PROPO_MAN", "");

		ds_list.SetColumn(ds_list.currow, "CHK","");
		ds_list.SetColumn(ds_list.currow, "SEQNO", "");

	    gfn_showMsg("CW10025","해당 호기는 신청 대상에 올라가 있습니다. 중복 입력은 불가합니다.");
	    }

		obj.fireevent = true;
	}
//alert(ds_list.SaveXML());			
	else if ( strColumnID == 'LIFNR_AF' )
	{
		//obj.fireevent = false;
		//ds_list.SetColumn(nRow,"ZZTEMNO","");
		ds_list.SetColumn(ds_list.currow,"TEMNO","");
		fn_changeZztemno_grd_cd(ds_list.GetColumn(ds_list.currow,strColumnID));

		//obj.fireevent = true;
		break;

	}
}



/********************************************************************************
* 기      능   : 신청
********************************************************************************/
function fn_request() 
{

	var cnt = 0;

	if ( !gfn_isChangeDs(ds_list, true, ""))
	{	
		return;
	}

	ds_list_tmp.DeleteAll();
	//gfn_initDs(ds_list_tmp, true);	

	for( var i=0; i<ds_list.rowcount; i++ )
	{

		if (ds_list.GetColumn(i,"CHK") == "1")
		{				
			if ( length(ds_list.GetColumn(i,"POSID")) == 0)
			{				
				gfn_showMsg("CW00001","호기번호");
				return;
			}

			if ( length(ds_list.GetColumn(i,"LIFNR_AF")) == 0)			
			{			
				gfn_showMsg("CW00001","변경후 업체");
				return;
			}
			if ( length(ds_list.GetColumn(i,"TEMNO")) == 0)			
			{			
				gfn_showMsg("CW00001","팀장");
				return;
			}

			/*
			if (length(ds_list.GetColumn(i,"FILE_INDC")) == 0)
			{				
				gfn_openMsgWin("["+ds_list.GetColumn(i, "POSID")+"]호기 첨부파일 누락\n해당 신청호기를 더블클릭 후\n첨부파일을 등록해 주세요.","W","A","");

				return;
			}*/

			ds_list_tmp.AddRow();
			ds_list_tmp.CopyRow(cnt, "ds_list", i);

			cnt = cnt + 1;

		}

	}

//trace(ds_list_tmp.SaveXML());
//alert(ds_list_tmp.SaveXML());
    //선택한 행이 1건 이상일때문 실행
	if ( cnt > 0 )
	{
		tit_clearActionInfo();
