/********************************************************************************
* 기      능   : 완료
********************************************************************************/
function fn_finish() 
{

	var cnt = 0;

	if ( !gfn_isChangeDs(ds_list, true, ""))
	{	
		return;
	}

	ds_list_tmp.DeleteAll();
	//gfn_initDs(ds_list_tmp, true);	

	for( var i=0; i<ds_list.rowcount; i++ )
	{

		if (ds_list.GetColumn(i,"CHK") == "1")
		{				
			if ( length(ds_list.GetColumn(i,"POSID")) == 0)
			{				
				gfn_showMsg("CW00001","호기번호");
				return;
			}

			if ( length(ds_list.GetColumn(i,"LIFNR_AF")) == 0)			
			{			
				gfn_showMsg("CW00001","변경후 업체");
				return;
			}
            if ( ds_list.GetColumn(i,"FINAL_CHK") == 'X')			
			{			
				//gfn_showMsg("CW10025","발주현황을 확인하시기 바랍니다.");
				//return;
				//ds_list.SetColumn(i,"CHK",'X');
			}

			 if(ds_list.GetColumn(i,"L_PRO_R") < ds_list.GetColumn(i,"P_PRO_R")) // 최종 공정율 < 기성 지급율일시 이관불가
			 {
			    gfn_showMsg("CW10025","기성 지급율을 확인하시기 바랍니다.");
				return;
			 }

			 if(ds_list.GetColumn(i,"BAL")  == "있음") //  업체 이관 진행 현장  불가  
			 {
			    gfn_showMsg("CW10025","업체 이관 승인진행 건 입니다.");
				return;
			 }

			// 호기 중복 건 체크 
			 for (var j = i+1; j < ds_list.GetRowCount(); j++)	{

              if (ds_list.GetColumn(i,"POSID") == ds_list.GetColumn(j,"POSID"))	{
              var arr = [ "프로젝트 호기 중복 등록되어 있습니다. 확인바랍니다."];
				gfn_showMsg("CW10025",arr);
				return false;
              }        
            }	


			ds_list_tmp.AddRow();
			ds_list_tmp.CopyRow(cnt, "ds_list", i);

			cnt = cnt + 1;

		}

	}

   //완료 전 유효성 체크 
	if (fn_finish_check())	{
	//alert("a");
	//return;
	}
	//미발주건 체크
    ds_list_tmp3.Filter("TWBTR<>0 OR CHK=='X'");
	ds_list_tmp4.copyF(ds_list_tmp3);
	ds_list_tmp3.unFilter();

 // alert(ds_list_tmp3.rowcount() );

   //trace(ds_list_tmp4.SaveXML());
//alert(ds_list_tmp.SaveXML());
    //선택한 행이 1건 이상일때문 실행
 if ( cnt > 0  && ds_list_tmp3.rowcount() > 0)
	{
		tit_clearActionInfo();
		//tit_addSingleActionInfo("ps04:PS0420001A_U2");
		tit_addMultiActionInfo("ps04:PS0420001A_U4");
		//tit_addMultiActionInfo("ps04:PS0420001A_I2");
		//tit_addMultiActionInfo("ps04:PS0420001A_I3");
//		tit_addSelectExActionInfo("ps04:PS0420001A_S3", "ps04:PS0420001A_I1", "ps04:PS0420001A_U1");

		tit_callService(
			""
			, ""
			, "ds_list_tmp=ds_list_tmp"// ds_list_tmp4=ds_list_tmp4"
			, ""
			, ""
			, ""//"fn_afterSave1"
			, ""
			, ""
			, true  // ""  

		);
	}
	if ( cnt > 0  && ds_list_tmp4.rowcount() > 0)
	{
		tit_clearActionInfo();
		tit_addMultiActionInfo("ps04:PS0420001A_I2");
		tit_addMultiActionInfo("ps04:PS0420001A_I3");
//		tit_addSelectExActionInfo("ps04:PS0420001A_S3", "ps04:PS0420001A_I1", "ps04:PS0420001A_U1");

		tit_callService(
			""
			, ""
			, "ds_list_tmp4=ds_list_tmp4"
			, ""
			, ""
			, //"fn_afterSave1"
			, ""
			, ""
			, true  // ""  

		);
	}

 fn_query();
}


