function fn_finish_check()	{

   ds_list_tmp2.ClearData();
   ds_list_tmp3.ClearData();
 //  ds_cond2.DeleteAll();
   for( var i=0; i<ds_list.GetRowCount(); i++ )
	{			
		if (ds_list.GetColumn(i,"CHK") == "1")
		{
		   if(ds_cond2.GetRowCount()==0)	ds_cond2.AddRow();
		     ds_cond2.SetColumn(0,"PSPID",ds_list.GetColumn(i,"PSPID"));
	         ds_cond2.SetColumn(0,"POSID",ds_list.GetColumn(i,"POSID"));

	            tit_clearActionInfo();
		        tit_addSearchActionInfo("ps04:PS0420001A_S4");
		        tit_callService(
			     ""
			   , ""
			   , "ds_cond2=ds_cond2"
			   , "ds_list_tmp2=ds_list_tmp2"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	




			  ds_list_tmp2.SetColumn(0, "LIFNR_T", ds_list.GetColumn(i, "LIFNR_AF"));
			  ds_list_tmp2.SetColumn(0, "TEMNO_T", ds_list.GetColumn(i, "TEMNO"));
		      ds_list_tmp2.SetColumn(0,"NAMET_T",ds_zztemno_grd_cd.GetColumn(ds_zztemno_grd_cd.FindRow("TEMNO", ds_list.GetColumn(i, "TEMNO")), "NAMET"));  //항목에 text 저장 
			    //if(ds_cond2.GetRowCount()==0)	ds_cond2.AddRow();  

			  ds_list_tmp3.AppendDataset(ds_list_tmp2);

	    }
	}

	//trace(ds_list_tmp3.SaveXML());

}
