/********************************************************************************
* 기      능   : 그리드 더블클릭 시, 첨부파일 추가보기 팝업으로 연결
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 2 || nCell == 3 || nCell == 21)	// 프로젝트 호기, 프로젝트 명 더블 클릭 시
	{
		var sTransact = ds_list.GetColumn(nRow, "TRANSACT");
		if(sTransact == '')
		{
			gfn_openMsgWin("저장 후, 첨부파일을 등록해 주세요.","","A");
			return;
		}

		var formID="";

		formID = "PS0420001B";  
		//break;
		var sPspid     = ds_list.GetColumn(nRow, "PSPID");
		var sPosid     = ds_list.GetColumn(nRow, "POSID");
		var sZsiteNm   = ds_list.GetColumn(nRow, "ZSITE_NM");

		var arg = "POSID="+quote(sPosid);
			arg += " ZSITE_NM="+quote(sZsiteNm);
			arg += " PSPID="+quote(sPspid);



		gfn_openPopup(formID, true, arg);
	}
}

function btn_lifnr_OnClick(obj)
{
	//var arg = ds_list.GetColumn(nRow, "WRKLFN");
	var arg = 'F_ZZACTSS=""';
	var arr = gfn_openPopup("PS0208001D", true, arg);

	if(Length(arr) > 0)
	{
		//ds_list.fireevent = false;
		ds_cond.SetColumn(0, "LIFNR", arr[0]);
		ds_cond.SetColumn(0, "LIFNR_NM", arr[1]);
		//ds_list.fireevent = true;
	}
}

function ds_cond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'LIFNR_NM')
	{
		if(length(Trim(varNewValue)) == 0)
		{
			ds_cond.SetColumn(0, "LIFNR", "");
			ds_cond.SetColumn(0, "LIFNR_NM", "");

			return;
		}

		// 존재하는 사업자번호 체크 (미존재시 팝업)
		nFindRow = ufn_chkLifnr(varNewValue);

		if(nFindRow < 0)	// 사업자 번호 미존재시 팝업을 띄워준다.
		{
			var arg = "";
			var arr = gfn_openPopup("PS0208001D", true, arg);

			if(Length(arr) > 0)
			{
				ds_cond.SetColumn(0, "LIFNR", arr[0]);
				ds_cond.SetColumn(0, "LIFNR_NM", arr[1]);

			}

		}
		else
		{
			if(nFindRow == 1)
			{
				ds_cond.SetColumn(0, "LIFNR", ds_lifnr_grid.GetColumn(0, "CODE"));
				ds_cond.SetColumn(0, "LIFNR_NM", ds_lifnr_grid.GetColumn(0, "CODE_NAME"));

			}
			else
			{
				var arg = "F_LIFNR_N="+quote(varNewValue);
				var arr = gfn_openPopup("PS0208001D", true, arg);

				if(Length(arr) > 0)
				{
					ds_cond.SetColumn(0, "LIFNR", arr[0]);
					ds_cond.SetColumn(0, "LIFNR_NM", arr[1]);
				}
			}

		}
	}
}

function ufn_chkLifnr(lifnr)
{
	var rValue = 0;

	ds_condLifnr.ClearData();
	ds_condLifnr.addRow();
	ds_condLifnr.SetColumn(0, "LIFNR", lifnr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0208001D_S2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_condLifnr"
		, "ds_lifnr_grid=ds_lifnr_grid"
		, ""
		, "" // fn_afterQueryC
		, ""
		, ""
		, true);

	if(ds_lifnr_grid.GetRowCount() == 0)
	{
		rValue = -1;
	}
	else
	{
		rValue = ds_lifnr_grid.GetRowCount();
	}

	return rValue;
}


