/********************************************************************************
* 기      능   : 권한에 따라 활성화/비활성화
********************************************************************************/
function fn_authCheck(flg)
{
	if(ds_gbn.getColumn(0, "PSWEB_GBN") == 'A' )  // 검토자
	{
		btn_query.Enable = true;  //조회
        btn_request.Enable = false;  //신청
		btn_approval.Enable = true; //승인
		btn_sendback.Enable = true; //반송
		btn_finish.Enable = false; //완료	
		btn_add.Enable = false;
		btn_del.Enable = false;
	}	
	else if (ds_gbn.getColumn(0, "PSWEB_GBN") == 'B' )  //완료자 
	{
		btn_query.Enable = true;  //조회
        btn_request.Enable = false;  //신청
		btn_approval.Enable = true; //승인
		btn_sendback.Enable = true; //반송
		btn_finish.Enable = true; //완료	
		btn_add.Enable = false;
		btn_del.Enable = false;
	}	
	else  //신청자 
	{
		btn_query.Enable = true;  //조회
        btn_request.Enable = true;  //신청
		btn_approval.Enable = false; //승인
		btn_sendback.Enable = false; //반송
		btn_finish.Enable = false; //완료	
	}



}



