	fn_query1();

}
/********************************************************************************
* 기      능   : 조회(프로젝트 정보)
********************************************************************************/
function fn_query1() 
{

	// 필수 입력사항 및 일자 체크
	if (ed_pspid.Text == '')
	{ 	

		if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) )
		{   

			return;
		}
		else
		{
			if(gfn_isNull(cal_datef, true, '신청일자 또는 프로젝트번호는 필수입니다.')) return;
		}

		btn_add.Enable = false;
		btn_del.Enable = false;

	}

	//프로젝트 번호가 있어야지 추가가 가능함
	else 
	{
		if( ds_gbn.getColumn(0, "PSWEB_GBN") == 'A' || ds_gbn.getColumn(0, "PSWEB_GBN") == 'B' )
		{

		}
		else
		{
			btn_add.Enable = true;
			btn_del.Enable = true;
		}
    }



	// 조회 처리
	//if( ds_gbn.getColumn(0, "PSWEB_GBN") == '' )
	if( length(ds_gbn.getColumn(0, "PSWEB_GBN")) == 0 )
	{
		ds_cond.SetColumn(0,"PSWEB_GBN",'Y');
	}
	else
	{
		ds_cond.SetColumn(0,"PSWEB_GBN",'X');
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0420001A_C4"); 
	tit_addSearchActionInfo("ps04:PS0420002A_S1"); 
	tit_addSearchActionInfo("ps04:PS0420002A_S2");     	
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_posid=ds_posid ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);

}


