function fn_afterSave1(errCode, errMsg) {
	if ( errCode != 0 ) {
	//alert("[" + errCode + "]");
		gfn_showMsg("CE00001");
	} else {
	//alert("bbb");
		gfn_showMsg("CI00003");		
		fn_query();
	}
}





