function fn_OnLoadCompleted(obj) {

	//alert("fn_OnLoadCompleted");

	// Form 로드 이벤트 맨 첫줄에 기술 
	//gfn_initForm(true);	
	gfn_initForm();	


	PER_GBN = PER_GBN;
	PRE_MM = PRE_MM + "01";
	PRE_DATE = PRE_DATE;
	PRE_PJT = PRE_PJT;
	PER_GUBUN = PER_GUBUN;
//ALERT(PER_GUBUN);
    ds_cond.SetColumn(0,"GUBUN",PER_GUBUN);
	ds_authority.ClearData();
	ds_closedate.ClearData();

	tit_clearActionInfo();	
	tit_addSearchActionInfo("bs04:BS0409001B_S7");     // 권한    
	tit_addSearchActionInfo("bs04:BS0409001B_S9");     // 마감일
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_authority=ds_authority  ds_closedate=ds_closedate"
		, ""
		, //"fn_afterQuery"
		, ""
		, ""
		, true);

	//마감버튼
	if(ds_authority.GetRowCount() > 0)
	{
		bt_closed.Visible = true;
		cb_closed.Enable = true;
	}
	else
	{
		bt_closed.Visible = false;
		cb_closed.Enable = false;
	}





	if(PER_GBN == "C")
	{
		ed_pjt.text = PRE_PJT;	
		//var lv_mm = PRE_MM + '01';
		cb_MAKTDT.text = PRE_MM  ;
		ds_cond.SetColumn(0,"MAKE_MM",PRE_MM);

		div_btn.btn_query.visible = false;

		ed_erdat.Text = substr(PRE_DATE,0,4) + "." + 
		                substr(PRE_DATE,4,2) + "." + 
		                substr(PRE_DATE,6,2);



		ufn_enable("C");
		fn_query();


	}
	else if(PER_GBN == "N")
	{
	    ds_vkgrp.Filter("1==2");
		div_btn.btn_query.visible = true;
		ed_erdat.Text = substr(gfn_getCurrDate(),0,4) + "." + 
		                substr(gfn_getCurrDate(),4,2) + "." + 
		                substr(gfn_getCurrDate(),6,2);
		ufn_enable("N");
	}
// 디테일row 추가 담당자만 가능하도록 추가(김종욱 sw 협의 // 김종욱-> 양성훈 담당자변경) //2018 주차담당자추가 -- 2018.09.17 문진원 GJ 추가
  if(G_USER_ID == "2024586"|| G_USER_ID == "2018900"||G_USER_ID == "2022702" || G_USER_ID == '2016885' || G_USER_ID == '2016737' || G_USER_ID == "2028875" || G_USER_ID == '2029847' || G_USER_ID == '2026473' || G_USER_ID == '2026481') {  
	  btn_add.Visible = true;
	}else {
	  btn_add.Visible = false;
	}

}

function ufn_enable(gbn)
{
	if(gbn=="C")
	{
		ed_pjt.Enable = false;
		btn_pjt.Enable = false;

		cb_MAKTDT.Enable = true;
		cd_action.Enable = false;
		div_btn.btn_query.visible = false;
	}
	else if(gbn=="N")
	{   	    
	    cb_MAKTDT.Enable = true;
	    cd_action.Enable = true;
	    div_btn.btn_query.visible = true;

	}


}


