		, ""
		, ""
		, true);


	if(PER_GBN == "C")	// 수정/조회 
	{

//ALERT("A");	
		//if(gfn_isNull(ed_pjt, true, Static1.Text)) return;
		//if(gfn_isNull(cb_MAKTDT, true, Static12.Text)) return;

		//if(gfn_isNull(ed_tdept, true, Static38.Text)) return;
		//if(gfn_isNull(ed_team, true, Static5.Text)) return;
		//if(gfn_isNull(cb_person, true, Static4.Text)) return;


        //ds_cond.SetColumn(0,"GUBUN",PER_GUBUN);


		tit_clearActionInfo();       
		//tit_addSearchActionInfo("bs04:BS0409004B_S1");         		   
		tit_addSearchActionInfo("ps04:PS0440001B_S1");         		   
		//tit_addSearchActionInfo("bs04:BS0409004B_S2");     
		tit_addSearchActionInfo("ps04:PS0440001B_S2");         		   
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master ds_detail=ds_detail"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);
//ALERT("B");			

	}
    else if(PER_GBN == "N")	// 신규 입력
    {


		//if(gfn_isNull(ed_pjt, true, Static1.Text)) return;
		  if(ToNumber(ds_psd.GetColumn(0,"CNT")) >= 1){
  		  gfn_showAlert("CW10025","해당 프로젝트는 현대무벡스로 이관되어 작업이 불가합니다.");
