		//ds_cond.SetColumn(0,"GUBUN",PER_GUBUN);

		if(PER_GUBUN == 'ES')
		{

			tit_clearActionInfo();	
			tit_addSearchActionInfo("ps04:PS0440001B_S6");   
			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk3=ds_chk3"
				, ""
				, ""
				, ""
				, ""
				, true);																		
		}

		if(ds_chk3.GetColumn(0, "CHK3") > 0)
		{

			ds_cond.SetColumn(0,"CHK3", "N" );
		}
		else		
		{


			ds_cond.SetColumn(0,"CHK3", "Y" );
		}


		tit_clearActionInfo();		     		       		      
		//tit_addSearchActionInfo("bs04:BS0409004B_S3");     


