				var lv_RETREN_RATE = round((ToFloat(ds_detail.GetColumn(i,"RETREN_AMT")) / ToFloat(ds_detail.GetColumn(i,"NETWR"))) * 100 ,1) ;	
				ds_detail.SetColumn(i, "RETREN_RATE",  lv_RETREN_RATE);
