/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;

	if(PER_GBN == "C")
	{
		if (!gfn_showConfirm("CI00015","MR보고서")) 
		{
			return;
		}	


		tit_clearActionInfo();	

		//tit_addSingleActionInfo("bs04:BS0409004B_D2");						
		tit_addSingleActionInfo("ps04:PS0440001B_D2");						

		//tit_addSingleActionInfo("bs04:BS0409004B_D1");	
		tit_addSingleActionInfo("ps04:PS0440001B_D1");	

		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }


}


/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() 
{
	ds_print.ClearData();
    if(ds_detail.GetRowCount() > 0)
    {
		gfn_initDs(ds_print, true);	    
		//ds_print.copy(ds_master);
		ds_master.CopyToRow(0,"ds_print",0);

		//alert(cb_tdept.text);

		ds_print.SetColumn(0, "DEPT", cb_tdept.text);
		ds_print.SetColumn(0, "TEAM", cb_team.text);
		ds_print.SetColumn(0, "PERSON_NM", cb_person.text);


		for(var i=0; i<ds_detail.GetRowCount(); i++)
		{
			if(i>0)
			{
				ds_print.AddRow();			
			}

			ds_print.SetColumn(i,"KSTAR_D", ds_detail.GetColumn(i,"KSTAR"));			
			ds_print.SetColumn(i,"KTEXT_D", ds_detail.GetColumn(i,"KTEXT"));						
			ds_print.SetColumn(i,"NETWR_D", ds_detail.GetColumn(i,"NETWR"));			
			ds_print.SetColumn(i,"WAVWR_D", ds_detail.GetColumn(i,"WAVWR"));			
			ds_print.SetColumn(i,"SAYU_D", ds_detail.GetColumn(i,"SAYU"));
			ds_print.SetColumn(i,"HOGI_D", ds_detail.GetColumn(i,"HOGI"));					
			ds_print.SetColumn(i,"RETREN_AMT_D", ds_detail.GetColumn(i,"RETREN_AMT"));
			ds_print.SetColumn(i,"RETREN_RATE_D", ds_detail.GetColumn(i,"RETREN_RATE"));	

			ds_print.SetColumn(i,"DMR_NETWR_D", ds_detail.GetColumn(i,"DMR_NETWR"));					
			ds_print.SetColumn(i,"WD_END_D", ds_detail.GetColumn(i,"WD_END"));					
			ds_print.SetColumn(i,"FINISH_D", ds_detail.GetColumn(i,"FINISH"));					
		}


    }
    else
    {
		gfn_initDs(ds_print, true);	    
		//ds_print.copy(ds_master);
		ds_master.CopyToRow(0,"ds_print",0);
    }




	Dialog("ps04::PS0440001P.xml", "");
	//ds_print.ClearData();
}





