		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}




	ds_cond.SetColumn(0,"USER_GRUP",userGroup);

	if (userGroup == "10")
	{ 
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_USER_ID);
	}
	else if (userGroup == "30")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_AREA_CODE_E);
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


///*
	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_lifnr_cd=ds_lifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true);

}



/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {



  //if(gfn_isNull(ed_pjt, true, st_pjt.Text)) return
	if ( !gfn_checkFromToObj(cal_f_from, cal_f_to, true, st_date.value, 0, true)   ) 
	{
		return;
	}

	tit_clearActionInfo();
	if(Radio1.Value == "1")
	{  
	   ds_list.ClearData();
	   grd_list.Format = "Default";
       tit_clearActionInfo();
 	   tit_addSearchActionInfo("ps04:PS0450001A_S1"); 
	   }
	else
	{
		ds_list.ClearData();
		grd_list.Format = "Default1";
        tit_clearActionInfo();
		tit_addSearchActionInfo("ps04:PS0450001A_S2");
	}

	 tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true );    




}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
   //   gfn_showSearchMsg(grd_list);
   gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
//    ds_lifnr_cd.Filter("LOEVM==''");
//	gfn_insertEmptyRow( ds_lifnr_cd, G_CODE_ALL );
//	cb_lifnr.value = "";
//	cb_lifnr.Index = 0;
	cb_zzactss.Index = 0;
	Radio1.Index = 1;	

	cb_lifnr.Index = 0;

	if ( userGroup == "10" )
	{ 
		cb_lifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
		ds_lifnr_cd.Filter("LOEVM==''");
		gfn_insertEmptyRow( ds_lifnr_cd, G_CODE_ALL );
		cb_lifnr.value = "";
	}
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
	//	cb_zzactss.Enable = false;  -계원찬 SW요청 (사무소 활성화)
	}
}

