function Radio1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(Radio1.Index == 0)
	{
		btn_print.enable = false;
		btn_save.enable = false;
		btn_delete.enable = false;
		rd_chk.Visible = false;
		Static1.Visible = false;
		cb_period.Index = 0;
		cb_period.enable = false;	

	}
	else
	{
	    btn_print.enable = true;
	    btn_save.enable = true; 
	    btn_delete.enable = true;
	    rd_chk.Index= 2;
	    rd_chk.Visible = true;
	    Static1.Visible = true;
	    cb_period.enable = true;	
	}
	//fn_query();	
}

