// grd_list 작업완료일 달력 처리 부분  --2015.06.08 LEH 추가 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;

	if(nCell == 16)	{
		cal_temp.value = ds_list.getColumn(nRow, "ZFDAT");	
	} 
	cal_temp.SetFocus();
//	trace(ds_list2.SaveXML());
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 16)	{
		ds_list.setColumn(ds_list.row, "ZFDAT", strText);
	} 
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
		    if(length(ds_list.GetColumn(i, "FILNR")) == 0 )
		        {
                 // ds_list.Filter("CHECK==1");
	              ds_save.copyF(ds_list);
	              //ds_list.unFilter();

		        }
		     else
		       {
     		     global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
                 global.FRM_BOTTOM.st_msg.color = "yellow";       
                 global.FRM_BOTTOM.st_msg.value = "전표가 생성되어 작업완료일을 변경하실 수 없습니다.";
		         return;  
		       }
	      }
	  }

	   tit_clearActionInfo();
	   tit_addMultiActionInfo("ps04:PS0450001A_U1");
	   tit_callService(
	    ""
	  , ""
	  , "ds_list=ds_save"
	  , ""
	  , ""
	  , "fn_afterSave"
	  , true);

}


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}
	 fn_query();
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.fireevent = false;
	if(strColumnID!="CHECK"){
		ds_list.SetColumn(nRow, "CHECK", 1);
	}
	ds_list.fireevent = true;
}

/********************************************************************************
* 기      능   : 삭제  -- 작업 완료일 리셋 
********************************************************************************/
function fn_delete() 
{

  if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
   }

   for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
		    if(ds_list.GetColumn(i, "ZFDAT") > 19990101 )
		        {
                  ds_list.Filter("CHECK==1");
	              ds_delete.copyF(ds_list);
	              ds_list.unFilter();

		        }
		     else
		       {
     		     global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
                 global.FRM_BOTTOM.st_msg.color = "yellow";       
                 global.FRM_BOTTOM.st_msg.value = " 작업완료일이 비어있습니다.";
		         return;  
		       }
	      }
	  }
      tit_clearActionInfo();
	   tit_addMultiActionInfo("ps04:PS0450001A_U2");
	   tit_callService(
	    ""
	  , ""
	  , "ds_list=ds_delete"
	  , ""
	  , ""
	  , "fn_afterDelete"
	  , true);

   //alert(trace(ds_delete.SaveXML()));
}
/********************************************************************************
* 기      능   : 삭제  Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}
	 fn_query();
}
