/********************************************************************************
* 기      능   : (+)버튼 클릭시
********************************************************************************/
function btn_add_OnClick(obj)
{


	if(length(trim(ds_master.GetColumn(0,"BSTNK"))) <= 0)
	{

		gfn_showMsg("CW00054");
		return;
	}	

	ds_detail.fireevent = false;
	ds_detail.AddRow();			

	ds_detail.SetColumn(ds_detail.currow,"PSPID", ds_cond.GetColumn(0,"PSPID"));
	ds_detail.SetColumn(ds_detail.currow,"MAKE_MM", ds_master.GetColumn(0,"MAKE_MM"));

	//ds_detail.SetColumn(ds_detail.currow,"GBN1", "A");
	//ds_detail.SetColumn(ds_detail.currow,"GBN2", "A");

	ds_detail.SetColumn(ds_detail.currow,"WAERK", ds_master.GetColumn(0,"WAERK"));

	//ds_detail.SetColumn(ds_detail.currow,"NETWR", 0);	
	//ds_detail.SetColumn(ds_detail.currow,"WAVWR", 0);



	ds_detail.fireevent = true;


}


/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/
function btn_del_OnClick(obj)
{

	if(length(trim(ds_master.GetColumn(0,"BSTNK"))) <= 0)
	{
		gfn_showMsg("CW00054");
		return;
	}



	ds_detail.DeleteRow(ds_detail.row);
	ufn_calculate();

}


/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00001");

	//권한, 마감, 본인작성  체크	 
	if(PER_GBN == "C")
	{	 
	    ds_oneseft.ClearData();
	    ds_authority.ClearData();
	    ds_closed.ClearData();
		tit_clearActionInfo();


		//tit_addSearchActionInfo("bs04:BS0409004B_S6");     // 본인작성  
		tit_addSearchActionInfo("ps04:PS0440001B_S7");     // 본인작성  

		tit_addSearchActionInfo("bs04:BS0409001B_S7");     // 권한    
		tit_addSearchActionInfo("bs04:BS0409001B_S8");     // 마감      
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_oneseft=ds_oneseft  ds_authority=ds_authority  ds_closed=ds_closed"
			, ""
			, //"fn_afterQuery"
			, ""
			, ""
			, true);


		//본인작성
		if(ds_oneseft.GetRowCount() <= 0)
		{
			div_btn.btn_delete.visible = false;
			div_btn.btn_save.visible = false;     
			cd_action.Enable = false;
			grd_list.Enable = false;
			//btn_add.Enable = false;
			btn_del.Enable = false;
			//cb_tdept.Enable = false;
			cd_exam.Enable = false;
			cb_team.Enable = false;
			cb_person.Enable = false;
		}


		//마감작성
		if(ds_closed.GetRowCount() > 0)
		{
			div_btn.btn_delete.visible = false;
			div_btn.btn_save.visible = false;    
			cd_action.Enable = false;
			grd_list.Enable = false;
			//btn_add.Enable = false;
			btn_del.Enable = false;
			//cb_tdept.Enable = false;
			cd_exam.Enable = false;
			cb_team.Enable = false;
			cb_person.Enable = false;
		}

//alert("3 : "+ ds_authority.GetRowCount());			
		//full 권한
		if(ds_authority.GetRowCount() > 0)
		{
//alert("c");		
			div_btn.btn_delete.visible = true;
			div_btn.btn_save.visible = true;        
			cd_action.Enable = true;
			grd_list.Enable = true;
			//btn_add.Enable = true;
			btn_del.Enable = true;
			//cb_tdept.Enable = true;
			cd_exam.Enable = true;
			cb_team.Enable = true;
			cb_person.Enable = true;
		}



	}




}




