/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{

	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false; // 프로젝트
	if(gfn_isNull(ed_bstnk, true, Static0.Text)) return false;
	if(gfn_isNull(cb_waerk, true, Static32.Text)) return false;

	//if(gfn_isNull(cb_tdept, true, Static38.Text)) return false;
	if(gfn_isNull(cb_team, true, Static5.Text)) return false;
	if(gfn_isNull(cb_person, true, Static4.Text)) return false;	

		  if(length(ds_master.GetColumn(0, "TYPE")) <=0 )
		{
			var arr = [parseInt(0) + 1, "MR유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 

		  if(length(ds_master.GetColumn(0, "ACTION")) <=0 )
		{
			var arr = [parseInt(0) + 1, "ACTIVE유형"];			
			gfn_showAlert("CW00224", arr);
			return;
		} 


	if(cb_MAKTDT.text <= cb_closed.text)
	{
		gfn_showAlert("CW00022","입력하신 활동월이 마감일 이전입니다.");
		return;
	}


	if(gfn_isNull(cb_MAKTDT, true, Static12.Text)) return false; 



    ds_chk.clearData();
	if(PER_GBN == "N")
	{

		tit_clearActionInfo();

		//tit_addSearchActionInfo("bs04:BS0409004B_S5"); 
		tit_addSearchActionInfo("ps04:PS0460001B_S8"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{

		gfn_showMsg("CW00137");
		return;
	}


	tit_clearActionInfo();					
	//tit_addSelectExActionInfo("bs04:BS0409004B_S4", "bs04:BS0409004B_I1", "bs04:BS0409004B_U1");
	tit_addSelectExActionInfo("ps04:PS0460001B_S9", "ps04:PS0460001B_I1", "ps04:PS0460001B_U1");

	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);

	ufn_enable("C");

    //parent.fn_query(); 


}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{

	    //detail 저장
		tit_clearActionInfo();	

		//tit_addSingleActionInfo("bs04:BS0409004B_D1");
		tit_addSingleActionInfo("ps04:PS0460001B_D1");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, //""
			, ""
			, ""
			,true  // ""
			);	


		if(ds_detail.GetRowCount() > 0)
		{

			tit_clearActionInfo();	

			//tit_addMultiActionInfo("bs04:BS0409004B_I2");
			tit_addMultiActionInfo("ps04:PS0460001B_I2");
			tit_callService(
				""
				, ""        
				, "ds_detail=ds_detail"
				, ""
				, ""
				, "fn_afterSave2"
				, ""
				, ""
				,true  // ""
				);	
		}


	}
}

