		}	


		tit_clearActionInfo();	

		//tit_addSingleActionInfo("bs04:BS0409004B_D2");						
		tit_addSingleActionInfo("ps04:PS0460001B_D2");						

		//tit_addSingleActionInfo("bs04:BS0409004B_D1");	
		tit_addSingleActionInfo("ps04:PS0460001B_D1");	

		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }


}


/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() 
{
	ds_print.ClearData();
    if(ds_detail.GetRowCount() > 0)
    {
		gfn_initDs(ds_print, true);	    
		//ds_print.copy(ds_master);
		ds_master.CopyToRow(0,"ds_print",0);


		//alert(cb_person.text);



		ds_print.SetColumn(0,"DEPT",cb_tdept.text);
		ds_print.SetColumn(0,"TEAM",cb_team.text);
		ds_print.SetColumn(0,"PERSON_NM",cb_person.text);


		for(var i=0; i<ds_detail.GetRowCount(); i++)
		{
			if(i>0)
			{
				ds_print.AddRow();			
			}

			ds_print.SetColumn(i,"ITEM_D", ds_detail.GetColumn(i,"ITEM"));
    	    ds_print.SetColumn(i,"ITEMNO_D", ds_detail.GetColumn(i,"ITEMNO"));		
			ds_print.SetColumn(i,"CONTENT_D", ds_detail.GetColumn(i,"CONTENT"));	
			ds_print.SetColumn(i,"NETWR_D", ds_detail.GetColumn(i,"NETWR"));
			ds_print.SetColumn(i,"GBN1_D", ds_detail.GetColumn(i,"GBN1"));
			ds_print.SetColumn(i,"BIGO_D", ds_detail.GetColumn(i,"BIGO"));
			//ds_print.SetColumn(i,"GUBUN_D", ds_detail.GetColumn(i,"GUBUN"));
			ds_print.SetColumn(i,"ACTDAT_D", ds_detail.GetColumn(i,"ACTDAT"));
			ds_print.SetColumn(i,"ACTDAT2_D", ds_detail.GetColumn(i,"ACTDAT2"));

		}


    }
    else
    {
		gfn_initDs(ds_print, true);	    
		//ds_print.copy(ds_master);
		ds_master.CopyToRow(0,"ds_print",0);
    }




	Dialog("ps04::PS0460001P.xml", "");
	//ds_print.ClearData();
	//trace(ds_print.SaveXML());
}





/********************************************************************************
* 기      능   : 문자변환
********************************************************************************/

function ufn_replaceSpecialChar(inStr) {
	var str = "<br>";
	var rStr = inStr;
	for( var i = 0; i < length( str); i++ ) {
		rStr = replace(rStr, str[i], "\n");
	}

	return rStr;
}




