/********************************************************************************
* 기      능   : cb_MAKTDT 변경이벤트
********************************************************************************/
function cb_MAKTDT_OnChanged(obj,strOldText,strNewText)
{
	ds_cond.SetColumn(0,"MAKE_MM",cb_MAKTDT.Value);
	if(PER_GBN == "N")
	{   

		for(var i=0; i<ds_detail.GetRowCount(); i++)
		{
			ds_detail.SetColumn(i, "MAKE_MM", cb_MAKTDT.Value);
		}	

	}

}



/********************************************************************************
* 기      능   : 마감월 관리
********************************************************************************/
function bt_closed_OnClick(obj)
{

	tit_clearActionInfo();
	tit_addSearchActionInfo("bs04:BS0409001B_U2");     // 마감일 저장	
	tit_callService(
		""
		, ""
		, "ds_closedate=ds_closedate"
		, ""
		, ""
		, "fn_afterclosed"
		, ""
		, ""
		, true);


}


/********************************************************************************
* 기      능   : 마감일저장 Callback  
********************************************************************************/
function fn_afterclosed(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");	
	}
}



function cb_tdept_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	ds_vkgrp.filter("GUBUN="+quote( strCode ) );

}


function btn_mr_OnClick(obj)
{
	gfn_openPopup("BS0410004B",true);
}
