function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);


	PER_GBN = PER_GBN;
	PRE_MM = PRE_MM + "01";
	//PRE_DATE = PRE_DATE;
	PRE_PJT = PRE_PJT;



	ed_pjt.SetFocus();

	gfn_showMsg("CI00007");

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-3,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;


		if(PER_GBN == "C")
	{
		ed_pjt.text = PRE_PJT;	
		var lv_mm = PRE_MM + '01';
		cb_MAKTDT.text = PRE_MM  ;
		ds_cond.SetColumn(0,"MAKE_MM",PRE_MM);

		div_btn.btn_query.visible = false;

		//ed_erdat.Text = substr(PRE_DATE,0,4) + "." + 
		   //             substr(PRE_DATE,4,2) + "." + 
		    //            substr(PRE_DATE,6,2);




		ufn_enable("C");
		fn_query();


	}
	else if(PER_GBN == "N")
	{
		div_btn.btn_query.visible = true;
		//ed_erdat.Text = substr(gfn_getCurrDate(),0,4) + "." + 
		 //               substr(gfn_getCurrDate(),4,2) + "." + 
		   //             substr(gfn_getCurrDate(),6,2);
		ufn_enable("N");
	}


}

function ufn_enable(gbn)
{
	if(gbn=="C")
	{
		ed_pjt.Enable = false;
		btn_pjt.Enable = false;

		cb_MAKTDT.Enable = false;

		div_btn.btn_query.visible = false;
	}
	else if(gbn=="N")
	{   	    
	    cb_MAKTDT.Enable = true;

	    div_btn.btn_query.visible = true;

	}


}



