function fn_query() {
	//필수체크	
	ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));



	if(gfn_isNull(ed_pjt, true, st_pjt.value))	return;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0470001A_S2");
	tit_addSearchActionInfo("ps04:PS0470001A_S4");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_cnt=ds_cnt ds_cnt2=ds_cnt2"
        , ""
        , "fn_afterQuery"
        , true
        , true);

}
