      || length(trim(ds_master.GetColumn(0,"UNIT13"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT14"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT15"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT16"))) <= 0
      || length(trim(ds_master.GetColumn(0,"UNIT17"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT18"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT19"))) <= 0
      || length(trim(ds_master.GetColumn(0,"UNIT20"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT21"))) <= 0 || length(trim(ds_master.GetColumn(0,"UNIT22"))) <= 0
