       || length(trim(ds_master.GetColumn(0,"NETWR10"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR11"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR12"))) <= 0
       || length(trim(ds_master.GetColumn(0,"NETWR13"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR14"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR15"))) <= 0
       || length(trim(ds_master.GetColumn(0,"NETWR16"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR17"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR18"))) <= 0
       || length(trim(ds_master.GetColumn(0,"NETWR19"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR20"))) <= 0 || length(trim(ds_master.GetColumn(0,"NETWR21"))) <= 0
