/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {


	if(errCode == 0)
	{
		tit_clearActionInfo();
		if(PER_GBN == "C")
			tit_addSearchActionInfo("ps04:PS0470001A_S3");      

		else if(PER_GBN == "N")
		{
		    if(ds_cnt.GetColumn(0, "CNT") > 0 )
		     	tit_addSearchActionInfo("ps04:PS0470001A_S13");
		    else 
		         tit_addSearchActionInfo("ps04:PS0470001A_S1");
		}	
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_head=ds_head"
			, ""
			, "fn_afterQuery2"
			, ""
			, true);
	}		
	//trace(ds_head.SaveXML());


}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	//alert(ds_cnt2.GetColumn(0,"CNT2"));
	if(errCode == 0)
	{
		tit_clearActionInfo();
		if(PER_GBN == "C")
			tit_addSearchActionInfo("ps04:PS0470001A_S6");      

		else {
		       if(ds_cnt2.GetColumn(0, "CNT2") > 0 )  
			     tit_addSearchActionInfo("ps04:PS0470001A_S14");

			   else
			      tit_addSearchActionInfo("ps04:PS0470001A_S5");
	        }
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery3"
			, ""
			, true);
	}		


}

