/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(ed_pjt, true, st_pjt.Text)) return false; // 프로젝트

	if(PER_GBN == "C")
	{
		if (!gfn_showConfirm("CI00015","산업안전보건관리비")) 
		{
			return;
		}	


		tit_clearActionInfo();	

		tit_addMultiActionInfo("ps04:PS0470001A_D2");	
		tit_addMultiActionInfo("ps04:PS0470001A_D1");						


		tit_callService(
			""
			, ""        
			, "ds_head=ds_head ds_master=ds_master"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }

	//fn_reset("D");

}  

function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}

