/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print()

{
// alert(ds_head.GetColumn(0,"SF_AMOUNT2"));
// alert(ds_master.GetColumn(0,"SF_AMOUNT22"));   		
  ds_print.ClearData();
  gfn_initDs(ds_print, true);	    
  ds_master.CopyToRow(0,"ds_print",0);

  ds_print.SetColumn(0,"CON_AMOUNT", ds_head.GetColumn(0,"CON_AMOUNT"));                                                                                                                                                                  
  ds_print.SetColumn(0,"SF_AMOUNT" , ds_head.GetColumn(0,"SF_AMOUNT"));
  ds_print.SetColumn(0,"SF_AMOUNT2", ds_head.GetColumn(0,"SF_AMOUNT2"));   
  ds_print.SetColumn(0,"SF_AMOUNT2", ds_master.GetColumn(0,"SF_AMOUNT2"));    
  ds_print.SetColumn(0,"AMOUNT_A"  , ds_head.GetColumn(0,"AMOUNT_A"));                                                                                                                                                                                                       
  ds_print.SetColumn(0,"AMOUNT_B"  , ds_head.GetColumn(0,"AMOUNT_B"));                                                                                                                                                                                                            
  ds_print.SetColumn(0,"AMOUNT_C"  , ds_head.GetColumn(0,"AMOUNT_C"));                                                                                                                                                                                                                                                     
  ds_print.SetColumn(0,"AMOUNT_D"  , ds_head.GetColumn(0,"AMOUNT_D"));                                                                                                                                                                                                                                                   
  ds_print.SetColumn(0,"AMOUNT_E"  , ds_head.GetColumn(0,"AMOUNT_E"));                                                                                                                                                                                                                                                         
  ds_print.SetColumn(0,"AMOUNT_F"  , ds_head.GetColumn(0,"AMOUNT_F"));                                                                                                                                                                                                                                                                                                  
  ds_print.SetColumn(0,"AMOUNT_G"  , ds_head.GetColumn(0,"AMOUNT_G"));     
  ds_print.SetColumn(0,"AMOUNT_H"  , ds_head.GetColumn(0,"AMOUNT_H"));    

  ds_print.SetColumn(0,"ZSITE_NM"  , ds_head.GetColumn(0,"ZSITE_NM"));  
  ds_print.SetColumn(0,"NAMET"     , ds_head.GetColumn(0,"NAMET")); 
  ds_print.SetColumn(0,"ZQNTY"     , ds_head.GetColumn(0,"ZQNTY"));    
  ds_print.SetColumn(0,"ADDR"      , ds_head.GetColumn(0,"ADDR"));    
  ds_print.SetColumn(0,"VALUE"     , ds_head.GetColumn(0,"VALUE"));      
  ds_print.SetColumn(0,"KUNNR_NM"  , ds_head.GetColumn(0,"KUNNR_NM"));    
  ds_print.SetColumn(0,"RATE"      , ds_head.GetColumn(0,"RATE"));    
  ds_print.SetColumn(0,"ZZCHAKD"   , ds_head.GetColumn(0,"ZZCHAKD"));    
  ds_print.SetColumn(0,"ZZCOMP2"   , ds_head.GetColumn(0,"ZZCOMP2"));    
  ds_print.SetColumn(0,"YYYYMMF"   , ds_head.GetColumn(0,"YYYYMMF"));    
  ds_print.SetColumn(0,"MAKE_MM"   , ds_head.GetColumn(0,"MAKE_MM"));    
  ds_print.SetColumn(0,"AMOUNT_SA" , ds_sum.GetColumn(0,"AMOUNT_SA"));
  ds_print.SetColumn(0,"AMOUNT_SB" , ds_sum.GetColumn(0,"AMOUNT_SB"));
  ds_print.SetColumn(0,"AMOUNT_SC" , ds_sum.GetColumn(0,"AMOUNT_SC"));
  ds_print.SetColumn(0,"AMOUNT_SD" , ds_sum.GetColumn(0,"AMOUNT_SD"));
  ds_print.SetColumn(0,"AMOUNT_SE" , ds_sum.GetColumn(0,"AMOUNT_SE"));
  ds_print.SetColumn(0,"AMOUNT_SF" , ds_sum.GetColumn(0,"AMOUNT_SF"));
  ds_print.SetColumn(0,"AMOUNT_SG" , ds_sum.GetColumn(0,"AMOUNT_SG"));
  ds_print.SetColumn(0,"AMOUNT_SH" , ds_sum.GetColumn(0,"AMOUNT_SH"));
  //ds_print.SetColumn(0,"PERNO"     , ds_perno.GetColumn(0,"PERNO"));
  //ds_print.SetColumn(0,"TYPE"     , ds_perno.GetColumn(0,"TYPE"));


   for(var j=0; j<ds_perno.GetRowCount(); j++)
    {
      if(j>0)
       { 
          ds_print.AddRow();		
        }

		  ds_print.SetColumn(j,"PERNO",ds_perno.GetColumn(j,"PERNO"));
          ds_print.SetColumn(j,"TYPE",ds_perno.GetColumn(j,"TYPE"));
          ds_print.SetColumn(j,"TODAY",ds_perno.GetColumn(j,"TODAY"));
          ds_print.SetColumn(j,"ANJUN",ds_perno.GetColumn(j,"ANJUN"));
      }        



  Dialog("ps04::PS0470001P.xml", "gbn=c", -1, -1, "Resize=true");


}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
