/********************************************************************************
* 기      능   : list 자동계산식
********************************************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
   ds_head.SetColumn(0, "AMOUNT_A",ds_master.GetColumn(0,"AMOUNT_A")) ;     
   ds_head.SetColumn(0, "AMOUNT_B",ds_master.GetColumn(0,"AMOUNT_B")) ;     
   ds_head.SetColumn(0, "AMOUNT_C",ds_master.GetColumn(0,"AMOUNT_C")) ;     
   ds_head.SetColumn(0, "AMOUNT_D",ds_master.GetColumn(0,"AMOUNT_D")) ;     
   ds_head.SetColumn(0, "AMOUNT_E",ds_master.GetColumn(0,"AMOUNT_E")) ;     
   ds_head.SetColumn(0, "AMOUNT_F",ds_master.GetColumn(0,"AMOUNT_F")) ;     
   ds_head.SetColumn(0, "AMOUNT_G",ds_master.GetColumn(0,"AMOUNT_G")) ;     
   ds_head.SetColumn(0, "AMOUNT_H",ds_master.GetColumn(0,"AMOUNT_H")) ;   

   ds_master.SetColumn(0, "NETWR1", round(ToFloat( ds_master.GetColumn(0,"UNIT1"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT1")),0)) ;
   ds_master.SetColumn(0, "NETWR2", round(ToFloat( ds_master.GetColumn(0,"UNIT2"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT2")),0)) ;
   ds_master.SetColumn(0, "NETWR3", round(ToFloat( ds_master.GetColumn(0,"UNIT3"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT3")),0)) ;
   ds_master.SetColumn(0, "NETWR4", round(ToFloat( ds_master.GetColumn(0,"UNIT4"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT4")),0)) ;
   ds_master.SetColumn(0, "NETWR5", round(ToFloat( ds_master.GetColumn(0,"UNIT5"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT5")),0)) ;
   ds_master.SetColumn(0, "NETWR6", round(ToFloat( ds_master.GetColumn(0,"UNIT6"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT6")),0)) ;
   ds_master.SetColumn(0, "NETWR7", round(ToFloat( ds_master.GetColumn(0,"UNIT7"))   *   ToFloat(ds_master.GetColumn(0,"AMOUNT7")),0)) ;
