/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
  //if(ds_list.GetColumn(ds_list.row,"FLAG") == "")

    if(Tab0.TabIndex == 0)
    {  
		ds_list.DeleteRow(ds_list.row);
    }
    else if(Tab0.TabIndex == 1)
    {
		ds_list2.DeleteRow(ds_list2.row);
    }
    else if(Tab0.TabIndex == 2)
    {
		ds_list3.DeleteRow(ds_list3.row);
    }
    else if(Tab0.TabIndex == 3)
    {
		ds_list4.DeleteRow(ds_list4.row);
    }
    else if(Tab0.TabIndex == 4)
    {
		ds_list5.DeleteRow(ds_list5.row);
    }
    else if(Tab0.TabIndex == 5)
    {
		ds_list6.DeleteRow(ds_list6.row);
    }
    else if(Tab0.TabIndex == 6)
    {
		ds_list7.DeleteRow(ds_list7.row);
    }
    else if(Tab0.TabIndex == 7)
    {
		ds_list8.DeleteRow(ds_list8.row);
    }
}
/********************************************************************************
* 기      능   : 금액 자동계산 
********************************************************************************/
function Tab0_tab1_grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
    //인건비+업무수당
	   if( ds_list.GetColumn(nRow,"UNIT") > 0 && ds_list.GetColumn(nRow,"AMT") > 0) {      
           var arr = ToFloat(ds_list.GetColumn(nRow,"AMT")) * ToFloat(ds_list.GetColumn(nRow,"UNIT"));     
	       ds_list.SetColumn(nRow,"NETWR" ,arr)  ; 
           ds_head.SetColumn(0,"NETWR_A", ds_list.Sum("NETWR"));
           ds_head.SetColumn(0,"NETWR2", ds_list.Sum("NETWR") + ds_list2.Sum("NETWR") + ds_list3.Sum("NETWR")
                                       + ds_list4.Sum("NETWR") + ds_list5.Sum("NETWR") + ds_list6.Sum("NETWR") 
	                                   + ds_list7.Sum("NETWR") + ds_list8.Sum("NETWR")); }	 
}

