           var arr = ToFloat(ds_list8.GetColumn(nRow,"AMT")) * ToFloat(ds_list8.GetColumn(nRow,"UNIT"));     
	       ds_list8.SetColumn(nRow,"NETWR" ,arr)  ; 
           ds_head.SetColumn(0,"NETWR_H", ds_list8.Sum("NETWR"));                     
           ds_head.SetColumn(0,"NETWR2", ds_list.Sum("NETWR") + ds_list2.Sum("NETWR") + ds_list3.Sum("NETWR")
                                       + ds_list4.Sum("NETWR") + ds_list5.Sum("NETWR") + ds_list6.Sum("NETWR") 
	                                   + ds_list7.Sum("NETWR") + ds_list8.Sum("NETWR")); }		
}
