/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

 if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
 else 
	{
	   gfn_showMsg("CE00003");
    }
}

/********************************************************************************
* 기      능   : 저장2 Callback  
********************************************************************************/
function fn_afterSave2(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");	
	    fn_query();
	}

}	

