/********************************************************************************
* 기      능   : SRM 조회 Callback  
********************************************************************************/
function fn_afterSRMQuery(errCode, errMsg) {
 // alert(ds_srm.GetColumn(0,"GGRUP"));

	var qry = "";
	var args = "";
	Trace(ds_srm.GetColumn(0,"GGRUP"));
     if(ds_srm.GetColumn(0,"CNT") >= 1 ) //(관리자권한)
     {  
         ds_cond.SetColumn(0,"CNT","Y");

        if(ds_srm.GetColumn(0,"GGRUP") == "ZMSRM01")  //설치부관리자 권한
        {
//          ds_cond.SetColumn(0,"CNT","Y");         
//          ds_cond.SetColumn(0,"GRUP","ZMSRM02");
            qry += " AND T.GGRUP IN  ('ZMSRM01' , 'ZMSRM02', 'ZMSRM04', 'ZMSRM06', 'ZMSRM07', 'ZMSRM10', 'ZMSRM12', 'ZMSRM15')" ;

        }
        else if(ds_srm.GetColumn(0,"GGRUP") == "ZMSRM03") //효율개선부 관리자 권한 
        { 
//          ds_cond.SetColumn(0,"CNT","Y");
//          ds_cond.SetColumn(0,"GRUP","ZMSRM04");
            qry += " AND T.GGRUP IN ('ZMSRM03' , 'ZMSRM04', 'ZMSRM12')" ;
        }
        else if(ds_srm.GetColumn(0, "GGRUP") == "ZMSRM08")
        {
			qry += " AND T.GGRUP IN ('ZMSRM08', 'ZMSRM09')" ;
        }
     } 

    args = " SQRY="+quote(qry);	

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps04:PS0490001A_S1");
	tit_callService(
          ""//"PDA_ACT"
        , ""
        , "ds_cond=ds_cond"//"ds_cond=ds_srm"
        , "ds_list=ds_list"
        , args	
        , ""//"fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_list.fireevent = false;
	grd_list.Redraw = false;

	for(var i=0; i < ds_list.GetRowCount(); i++) {
		if(ds_srm.FindRow("USERNUMB", ds_list.GetColumn(i,"USERN_ID")) == -1) {
			ds_list.DeleteRow(i);
			i--;
			continue;
		}
		if(cb_state.Value <> "" && ds_list.GetColumn(i,"STATE") <> cb_state.Value ) {
			ds_list.DeleteRow(i);
			i--;
			continue;
		}
	}
	grd_list.Redraw = true;
	ds_list.fireevent = true;

	uds_orgList.CopyF(ds_list);

	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

