/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{
 //alert("a");

	// if(gfn_isNull(ed_pjt, true, st_pjt.Text)) return false; // 프로젝트

  if(ds_list.FindRow("CHECK", 1)==-1)
   {
  // alert("b");
   	 gfn_showMsg('CW00046');
	  return;
   }

    ds_list.Filter("CHECK=='1'");
    ds_list2.CopyF(ds_list);
    ds_list.unFilter();



  for(var i=0; i<ds_list2.GetRowCount(); i++)
   {// alert("c");
	 if(ds_list2.GetColumn(i, "CHECK")==1)
	 {//alert("d");
	    if(ds_list2.GetColumn(i, "STATE") <> 'S')
	    {//alert("e");
		   gfn_showMsg('CW10025', "정지상태가 아니므로 삭제할 수 없습니다.");
		    return;
	     }
	  }



		tit_clearActionInfo();	
		tit_addSelectExActionInfo("ps04:PS0490001A_S2", "ps04:PS0490001A_I1", "ps04:PS0490001A_I2");	
		tit_callService(
	      ""
		, ""
		, "ds_list2=ds_list2"
 		, ""
		, ""
		, "fn_afterDelete"
		, ""
		, ""
    	, true  // ""
		);

//trace(ds_list2.SaveXML());

	//fn_reset("D");
//	alert(ds_cnt.GetColumn(0, "CNT"));

}  
}

