	  tit_callService(
	   ""
	 , ""        
	 , "ds_list2=ds_list2"
	 , ""
	 , ""
	 , ""//"fn_afterDelete"
	 , ""
	 , ""
	 ,true  // ""
	 );	



//alert("f");
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");		
		fn_afterSRMQuery();    
		//fn_close();
	}

}
