    {
		gfn_showAlert("CW00022","서울/경기 사무소 선택시 서울1/2팀중 하나는 선택하셔야 합니다.");
		return;
	}

    if(cb_actss.value == '02')
    {
		if(chk_so3.Value == '1' && chk_so4.Value == '1') //전체팀
		{
			ds_cond.SetColumn(0,"SO_GBN","");
		}
		else if (chk_so3.Value == '1' && chk_so4.Value == '0') //1팀
		{
			ds_cond.SetColumn(0,"SO_GBN","1TEM");
		}
		else if (chk_so3.Value == '0' && chk_so4.Value == '1') //2팀
		{
			ds_cond.SetColumn(0,"SO_GBN","2TEM");
		}
	}
*/

//trace( ds_cond.SaveXML());   
//alert("xxx");  
    tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0704010A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"  
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);


}



/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	for(var i=0; i<ds_list.GetRowCount(); i++)
	{

		if( ds_list.GetColumn(i, "ASK_DATE")=='00000000'|| ds_list.GetColumn(i, "ASK_DATE")=='' )
		{
			ds_list.SetColumn(i, "ASK_DATE", gfn_getCurrDate() );
		}

		if( ds_list.GetColumn(i, "REQ_DATE")=='00000000'|| ds_list.GetColumn(i, "REQ_DATE")=='' )
		{
			ds_list.SetColumn(i, "REQ_DATE", ds_list.GetColumn(i, "ASK_DATE") );		    
		}


		if( IndexOf(ds_list.GetColumn(i, "CHK_NAME1"), "관리원") != -1 )
		{			
			ds_list.SetColumn(i, "GUBUN", "1") ;
		}

		if( IndexOf(ds_list.GetColumn(i, "CHK_NAME1"), "기술원") != -1 )
		{			
			ds_list.SetColumn(i, "GUBUN", "2") ;
		}

	}

	// 메시지 처리 
	gfn_showSearchMsg(grd_list);
	//copy tool 활성화
	//btn_copy.Enable = true;
}


/********************************************************************************
* 기      능   : 버튼 활성화
********************************************************************************/
function fn_btn() 
{
	if( cb_gbn1 == '0') //신청
	{
		btn_save.Enable  	= true;
		btn_check.Enable 	= true;
		btn_delete.Enable 	= false;
		btn_sendback.Enable = false;
		btn_approval.Enable = false;
		btn_confirm.Enable  = false;
	}
	else if( cb_gbn1 == '1' ) //저장
	{
		btn_save.Enable  	= false;
		btn_check.Enable 	= true;
		btn_delete.Enable 	= true;
		btn_sendback.Enable = false;
		btn_approval.Enable = true;
		btn_confirm.Enable  = false;
	}
	else if( cb_gbn1 == '2' ) //승인
	{
		btn_save.Enable  	= false;
		btn_check.Enable 	= true;
		btn_delete.Enable 	= false;
		btn_sendback.Enable = true;
		btn_approval.Enable = false;
		btn_confirm.Enable  = true;
	}
	else if( cb_gbn1 == '3' ) //보류
	{
		btn_save.Enable  	= false;
		btn_check.Enable 	= false;
		btn_delete.Enable 	= true;
		btn_sendback.Enable = false;
		btn_approval.Enable = true;
		btn_confirm.Enable  = false;
	}
}


function cb_gbn1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_list.DELETEALL();
	fn_btn();
}

function cb_gbn2_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_list.DELETEALL();
	//fn_btn();	
}

function cb_gbn3_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_list.DELETEALL();
	//fn_btn();	
}


