/********************************************************************************
* 기      능   : 승인 10
********************************************************************************/
function fn_approval() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHECK")==1)
		{

			if(length(ds_list.GetColumn(i, "CHK_KIND"))<=0 )
			{
				var arr = [parseInt(i) + 1, "검사종류"];
				gfn_showMsg("CW00224", arr);
				return;
			}

			if(ds_list.GetColumn(i, "ASK_DATE")=='00000000'|| ds_list.GetColumn(i, "ASK_DATE")=='')
			{
				var arr = [parseInt(i) + 1, "신청일자"];
				gfn_showMsg("CW00224", arr);
				return;
			} 

			if(ds_list.GetColumn(i, "REQ_DATE")=='00000000'|| ds_list.GetColumn(i, "REQ_DATE")=='')
			{
				var arr = [parseInt(i) + 1, "검사요청일"];
				gfn_showMsg("CW00224", arr);
				return;
			} 

			if(length(ds_list.GetColumn(i, "PERNO"))<=0 )
			{
				var arr = [parseInt(i) + 1, "검사종류"];
				gfn_showMsg("CW00224", arr);
				return;
			}



		}
	}


	ds_list_tmp.ClearData();
	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();


	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps07:PS0704010A_S3","ps07:PS0704010A_I3", "ps07:PS0704010A_U7");   //ZPST0036P 최초 승인일 관리 
	tit_addMultiActionInfo("ps07:PS0704010A_U3");
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterSave2"
		, ""
		, ""
		, true);


}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}


	if( ds_gbn3.GetColumn(0, "CHECK")=='1')
	{
		if(!gfn_showConfirm( "CW10025", "영업 미승인 입니다. 계속 진행 하시겠습니까?" ))
		{
			return;	
		}
	}

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHECK")==1)
		{

			if(length(ds_list.GetColumn(i, "CHK_KIND"))<=0 )
			{
				var arr = [parseInt(i) + 1, "검사종류"];
				gfn_showMsg("CW00224", arr);
				return;
			}

			if(ds_list.GetColumn(i, "ASK_DATE")=='00000000'|| ds_list.GetColumn(i, "ASK_DATE")=='')
			{
				var arr = [parseInt(i) + 1, "신청일자"];
				gfn_showMsg("CW00224", arr);
				return;
			} 

			if(ds_list.GetColumn(i, "REQ_DATE")=='00000000'|| ds_list.GetColumn(i, "REQ_DATE")=='')
			{
				var arr = [parseInt(i) + 1, "검사요청일"];
				gfn_showMsg("CW00224", arr);
				return;
			} 

			if(length(ds_list.GetColumn(i, "PERNO"))<=0 )
			{
				var arr = [parseInt(i) + 1, "검사종류"];
				gfn_showMsg("CW00224", arr);
				return;
			}

		}
	}


	ds_list_tmp.ClearData();
	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();


	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0704010A_U4");
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);


}	
