/********************************************************************************
* 기      능   : sms 기능 추가 2015.04.21 문진원 GJ 요청 (LEH SW 수정)  -- 담당영업사원
********************************************************************************/
function fn_sms()
{

   for(var i=0; i<ds_list_tmp.GetRowCount(); i++)
   {     

     if(ds_list_tmp.GetColumn(i,"ZTEL")  != 0 || ds_list_tmp.GetColumn(i,"REQ_CELL")  != 0  )
	 {

     	var lv_msg = "";
     	lv_msg = ds_list_tmp.GetColumn(i,"ZSITE_NM") + ' 현장 ' + ds_list_tmp.GetColumn(i,"POSID") + '호기 완성검사 ' + substr(ds_list_tmp.GetColumn(i,"REQ_DATE"),0,4) +'.'+substr(ds_list_tmp.GetColumn(i,"REQ_DATE"),4,2)+'.'+substr(ds_list_tmp.GetColumn(i,"REQ_DATE"),6,2)+ ' 신청 되었습니다.';

       	gfn_initDs(ds_sms, true);
	    ds_sms.SetColumn(0,"TRAN_ETC1", "PS");
	    //ds_sms.SetColumn(0,"TRAN_TYPE", "1");
	    //ds_sms.SetColumn(0,"TRAN_STATUS", "1");
	    ds_sms.SetColumn(0,"TRAN_TYPE", "0");
	    ds_sms.SetColumn(0,"TRAN_STATUS", "0");
	    ds_sms.SetColumn(0,"TRAN_CALLBACK", "설치");
	    ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);
	    ds_sms.SetColumn(0,"TRAN_PHONE",ds_list_tmp.GetColumn(i,"ZTEL"));
	    ds_sms.SetColumn(0,"TRAN_PHONE2",ds_list_tmp.GetColumn(i,"REQ_CELL"));


     	tit_clearActionInfo();
     	tit_addMultiActionInfo("ps07:PS0704010A_I1");
     	tit_addMultiActionInfo("ps07:PS0704010A_I2");
	    tit_callService(
	   	 "PDA_ACT"
	    , ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, ""
		, ""
		, ""
		, true);
    }

  }
   fn_smsSend();

}
/********************************************************************************
* 기      능   : sms 메시지 Callback  
********************************************************************************/
function fn_smsSend(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송을 실패하였습니다.";
		return;
	} 
    else 
    {
		//global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		//global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송이 되었습니다.";
	}

  fn_query();
}

