/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{

	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;

	if(nCell == 18)	
	{	
		cal_temp.value = ds_list.getColumn(nRow, "REQ_DATE");	
	} 
	else if(nCell == 4)	
	{
		cal_temp.value = ds_list.getColumn(nRow, "CFM_DATE");	
	}	
	else if(nCell == 5)	
	{
		cal_temp.value = ds_list.getColumn(nRow, "FRO_DATE");	
	}
	cal_temp.SetFocus();

}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 18)	
	{
		ds_list.setColumn(ds_list.row, "REQ_DATE", strText);
	} 
	else if(grd_list.GetClickedCell() == 4)	
	{
		ds_list.setColumn(ds_list.row, "CFM_DATE", strText);
	}	
	else if(grd_list.GetClickedCell() == 5)	
	{
		ds_list.setColumn(ds_list.row, "FRO_DATE", strText);
	}

	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

