function cb_actss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode != '02')
	{
		chk_so3.Value = 0;
		chk_so4.Value = 0;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
	}
	else
	{
		chk_so3.Value = 1;
		chk_so4.Value = 1;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
	}
}
