function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	gfn_initDs(ds_cond, true);
	//copy tool 비활성화

	btn_copy.Enable = false;
	userGroup = gfn_getUserGroup();

	if(G_USER_GROUP_B == 'B10' || G_USER_GROUP_B == 'B30' || G_USER_GROUP_B == 'B40') userGroup = "40";

	cb_gbn.Index = 0;
	cb_gbn2.Index = 0;
    ck_15.Value = 0;
    ck_7.Value = 0;
    cb_text.index = 0;	
	// 날짜 셋팅
	var currYear = left(gfn_getCurrDate(), 6);
	cal_from.Value = currYear+"01";
	cal_to.Value = addDate((parseInt(currYear)+1)+"0101", -1);

	var codeArr = ["PS003"];
    var dsObjArr = [ds_combo];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0701001A_S3");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_combo2=ds_combo2"
		, ""
		, "fn_afterLifnr"
		, true,true);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "30")
	{
		ds_combo.filter("CODE == " + quote( G_AREA_CODE_E) );
	}
	cb_actss.Index = 0;		
	radio_wrbtr.Value = '1';

	if(userGroup == "30" && G_AREA_CODE_E == '02')
	{
		chk_so2.Enable = false;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = false;
		chk_so6.Enable = false;
	}
	else if( userGroup == "40" && cb_actss.Text == '- 전체 -' )
	{
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so6.Enable = true;
	}
	else
	{
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so6.Enable = false;	
	}
}
