/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if ( !gfn_checkFromToObj(cal_from, cal_to, true, st_date.value, 0, false) ) {
			return;
	}
	if(ck_15.Value == '1' && ck_7.Value == '1')
	{
	   gfn_showMsg("CI30001","완성검사 D-DAY 구분 값은 한개만 체크하세요.");
	   return;
	}
	ds_cond.SetColumn(0,"GBNA","");
	ds_cond.SetColumn(0,"GBNC","");
	if(cb_gbn.Value == 'A') ds_cond.SetColumn(0,"GBNA","A");
	else if(cb_gbn.Value == 'C') ds_cond.SetColumn(0,"GBNC","C");
	else {
		ds_cond.SetColumn(0,"GBNA","A");
		ds_cond.SetColumn(0,"GBNC","C");
	}

    if(ck_15.Value == '1' && ck_7.Value == '0') ds_cond.SetColumn(0,"DCHK","A");
		else if(ck_15.Value == '0' && ck_7.Value == '1') ds_cond.SetColumn(0,"DCHK","B");
		else  ds_cond.SetColumn(0,"DCHK","");
	ds_cond.SetColumn(0,"SO_GBN","");
	var qry = "";
	var args = "";

// 	서울/경기 사무소 TEAM 코드화
	if(userGroup == "30" && cb_actss.Value == '02')
	{
		//if(chk_so3.Value == '1' && chk_so4.Value == '0') ds_cond.SetColumn(0,"SO_GBN","A");
		//else if(chk_so3.Value == '0' && chk_so4.Value == '1') ds_cond.SetColumn(0,"SO_GBN","B");
	}
	else if( userGroup == "40" && cb_actss.Value == '' )
	{
		if(chk_so2.Value == '1' || chk_so5.Value == '1' || chk_so6.Value == '1') //&& (chk_so3.Value == '1' && chk_so4.Value == '0'))
		{
			qry += " AND (A.ZZACTSS IN (";
			if(chk_so2.Value == '1') qry+= "'01',";
			if(chk_so5.Value == '1') qry+= "'13',";
			if(chk_so6.Value == '1') qry+= "'14',";
			qry+= "'')) ";
			ds_cond.SetColumn(0,"SO_GBN","C");
		}	
	}
	args = " SQRY="+quote(qry);	

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0705002A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , args
        , "fn_afterQuery"
        , true
        , true);

     //   trace(ds_list.SaveXML());
}
