/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	// 대상 로우 체크
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
			// 삭제 대상이 아님
			if(ds_list.GetColumn(i, "DATE1")=='00000000' || ds_list.GetColumn(i, "DATE1")==''){
				gfn_showMsg('CW00075', ToString(i+1));
				return;
			}
			// 이미 승인된(검사완료일(준공일) 존재) 항목 - 삭제 불가
			if(ds_list.GetColumn(i, "DATE3")!='00000000' && ds_list.GetColumn(i, "DATE3")!=''){
				gfn_showMsg('CW00109', ToString(i+1)+ "행은 승인 ");
				return;
			}
		}
	}
	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
			ds_list.SetColumn(i, "DATE1", "00000000");
		}
	}

	ds_list.Filter("CHECK==1 && CHK_KIND=='A'");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	ds_list.Filter("CHECK==1 && CHK_KIND=='C'");
	ds_list3.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();			
	if(ds_list2.RowCount() > 0) tit_addMultiActionInfo("ps07:PS0705002A_D1");
	if(ds_list3.RowCount() > 0) tit_addMultiActionInfo("ps07:PS0705002A_D2");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterDelete"
		, true);	
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
     var old, new;
	// 대상 로우 체크
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}
	for(var i=0; i<ds_list.GetRowCount(); i++){ 
		if(ds_list.GetColumn(i, "CHECK")==1){
			// 필수 입력 항목
			old = ds_list.GetColumn(i, "DATE5");
			new = ds_list.GetColumn(i, "DATE4");
			if(ds_list.GetColumn(i, "DATE4")=='00000000'|| ds_list.GetColumn(i, "DATE4")==''){
				var arr = [parseInt(i) + 1, "검사요청일"];
				gfn_showMsg("CW00224", arr);
				return;
			}
		   if(old <> new && ds_list.GetColumn(i, "TXT03")=='' && ds_list.GetColumn(i, "CHK_KIND") == 'A') // 변경시 변경사유 필수 값 추가 (완성검사 일시)
		   {
		      var arr = [parseInt(i) + 1, "변경사유"];
			  gfn_showMsg("CW00224", arr);
			  return;
		   }
		}

	}

	ds_list.Filter("CHECK==1 && CHK_KIND=='A'");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	ds_list.Filter("CHECK==1 && CHK_KIND=='C'");
	ds_list3.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();			
	if(ds_list2.RowCount() > 0) 
	{
		tit_addMultiActionInfo("ps07:PS0705002A_U1");
		tit_addMultiActionInfo("ps07:PS0705001A_U1");
		if(old <> new)
		{//alert("요깅");
		  tit_addMultiActionInfo("ps07:PS0705002A_I1");
		}
	}	
	if(ds_list3.RowCount() > 0) 
	{
		tit_addMultiActionInfo("ps07:PS0705002A_U2");
		tit_addMultiActionInfo("ps07:PS0705001A_U2");
	}	

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);	
}	
