/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print1() 
{
	ds_list.Filter("CHECK==1");
	ds_list_3.copyF(ds_list);
	ds_list.unFilter();

	var pspid = "";
	var arg = "";
	var param = "";
	var kind = "";

	name1 = substr(ds_combo2.GetColumn(ds_combo2.FindRow("CODE",ds_list_3.GetColumn(0,"CODE2")),"CODE_NAME"),0,9);
	namec = substr(ds_combo2.GetColumn(ds_combo2.FindRow("CODE",ds_list_3.GetColumn(0,"CODE2")),"CODE_NAME"));
  //alert(name1);
  //alert(namec);
	for(var i=0; i<ds_list_3.GetRowCount(); i++){
		if(name1 != substr(ds_combo2.GetColumn(ds_combo2.FindRow("CODE",ds_list_3.GetColumn(i,"CODE2")),"CODE_NAME"),0,9))
		{
			gfn_showMsg("CW00204","검사기관");
			return;
		}

		if(pspid != "" && pspid != ds_list_3.GetColumn(i,"PSPID"))
		{
			gfn_showMsg("CW00204","프로젝트");
			return;
		}
		else pspid = ds_list_3.GetColumn(i,"PSPID");

		arg += "'"+ds_list_3.GetColumn(i,"POSID")+"'"+",";

		if(kind != "" && kind != ds_list_3.GetColumn(i,"CHK_KIND"))
		{
			gfn_showMsg("CW00204","검사종류");
			return;
		}
		else kind = ds_list_3.GetColumn(i,"CHK_KIND");

	}

	if(substr(arg,length(arg)-1) == ",") arg = substr(arg,0,length(arg)-1);

	param = "  AND P.POSID IN ("+arg+")";

	ds_cond_p.ClearData();
	ds_cond_p.AddRow();
	ds_cond_p.SetColumn(0,"GUBUN",param);
	ds_cond_p.SetColumn(0,"KIND",kind);
	ds_cond_p.SetColumn(0,"NAMEC",namec);
	ds_cond_p.SetColumn(0,"CODE2",ds_list_3.GetColumn(0,"CODE2"));

	ds_list_p.ClearData();
	tit_clearActionInfo();
	if(kind == 'A')
	{
		tit_addSearchActionInfo("ps07:PS0705002A_S2");
	}
	else if(kind == 'C')
	{
		tit_addSearchActionInfo("ps07:PS0705002A_S8");
		tit_addSearchActionInfo("ps07:PS0705002A_S9");

	}	

	tit_callService(
        ""
        , ""
        , "ds_cond_p=ds_cond_p"
        , "ds_list_p=ds_list_p ds_hogit=ds_hogit"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {

	for(var i = 0; i<ds_list_p.RowCount(); i++)
	{
		var hogit = "";
		var cnt = 0;
		for(var j = 0; j<ds_hogit.RowCount(); j++)
		{

			if(ds_list_p.GetColumn(i,"PSPID") == ds_hogit.GetColumn(j,"PSPID") &&
			   ds_list_p.GetColumn(i,"FLOOR") == ds_hogit.GetColumn(j,"FLOOR") &&
			   ds_list_p.GetColumn(i,"SPEC_GBN") == ds_hogit.GetColumn(j,"SPEC_GBN") )
			{
				if(cnt == 0) hogit += "(#"+ds_hogit.GetColumn(j,"HOGIT");
				else hogit += ","+ds_hogit.GetColumn(j,"HOGIT");
				cnt++;
			}
			if(cnt > 0 && ds_hogit.rowcount-1 == j) hogit += ")";
		}	
		ds_list_p.SetColumn(i,"HOGIT",hogit);
	}
//기술원 + 관리원 통합 2016.07.12
	  ds_list_p.SetColumn(0,"TXT",'한국승강기안전공단');
	  Dialog("PS07::PS0701002P.xml", "gbn=a", -1, -1, "Resize=true");

}
/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print2() 
{
	ds_list.Filter("CHECK==1");
	ds_list_3.copyF(ds_list);
	ds_list.unFilter();

	var kind = "";
	for(var i=0; i<ds_list_3.GetRowCount(); i++){

		if(kind != "" && kind != ds_list_3.GetColumn(i,"CHK_KIND") && kind == 'C')
		{
			gfn_showMsg("CW00204","검사종류");
			return;
		}
		else { kind = ds_list_3.GetColumn(i,"CHK_KIND");
	    }
	    if(ds_list_3.GetColumn(i,"CHAN") <> 'V')
		{
		    gfn_showMsg("CI30001","완성검사 일자 변경 현장을 선택하세요.");
			return;
		}

	}

	var pspid = "";
	var arg = "";
	var param = "";
	for(var i=0; i<ds_list_3.GetRowCount(); i++){
		arg += "'"+ds_list_3.GetColumn(i,"POSID")+"'"+",";
	}

	if(substr(arg,length(arg)-1) == ",") arg = substr(arg,0,length(arg)-1);

	param = "  AND P.POSID IN ("+arg+")";

	ds_cond_p.ClearData();
	ds_cond_p.AddRow();
	ds_cond_p.SetColumn(0,"GUBUN",param);

	ds_list_p.ClearData();
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0705002A_S11");
	tit_callService(
        ""
        , ""
        , "ds_cond_p=ds_cond_p"
        , "ds_list_p=ds_list_p"
        , ""
        , "fn_afterQuery3"
        , true);
}
