/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	//구분필드 필수체크	
	if(gfn_isNull(cb_type1, true, st_type1.value))
		return;
	//유형필드 필수체크	
	//if(gfn_isNull(cb_type2, true, st_type2.value))
		//return;
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) )
		return;
/*	
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
	if(cb_zzactss.value == '02' && chk_soa.Value == '0' && chk_sob.Value == '0') 
    {
		gfn_showAlert("CW00022","서울/경기 사무소 선택시 서울1/2팀중 하나는 선택하셔야 합니다.");
		return;
	}
	//=======================================================================================
	*/

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	// 조회 처리 
	tit_clearActionInfo();
	if(cb_type1.index = 0 ) //착공
	{
		if(cb_type2.index = 0 ) //신청
		{
			tit_addSearchActionInfo("ps07:PS0708001A_S1"); 
		}
		else if(cb_type2.index = 1 )  //조회
		{
			tit_addSearchActionInfo("ps07:PS0708001A_S3"); 
		}
	}
	else if(cb_type1.index = 1 ) //준공
	{
		if(cb_type2.index = 0 ) //신청
		{
			tit_addSearchActionInfo("ps07:PS0708001A_S2"); 
		}
		else if(cb_type2.index = 1 )  //조회
		{
			tit_addSearchActionInfo("ps07:PS0708001A_S3"); 
		}
	}

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"  
        , ""
        , "fn_afterQuery"  
        , ""
        , ""
        , true);
}


