/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
		// 현재 처리할 format 기억
		//gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}


/********************************************************************************
* 기      능   : 신청
********************************************************************************/
function fn_request() 
{

	var cnt = 0;
    var seqno = 0;
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	/*
	ds_list.Filter("CHK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();	



	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0708001A_I1");
	tit_callService(
         ""
       , ""
       , "ds_list_tmp=ds_list_tmp"
       , ""
       , ""
       , "fn_afterSave" 
       , ""
       , ""
       , true);

	ds_list_tmp.ClearData();		
	*/



	//ds_list_tmp.copyF(ds_list);
//TRACE(ds_list_tmp.SaveXML());	

    //SEQNO 값 읽어 오기
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0708001A_S5");

	tit_callService(
        ""
        , ""
        , "" //"ds_cond=ds_cond"
        , "ds_cnt=ds_cnt"
        , ""
        , ""
        , ""
        , ""
        , true
     );  

	seqno = ds_list.GetColumn(0, "SEQNO");


	ds_list_tmp.ClearData();	
	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHK")== 1)
		{

			//seqno = seqno + 1 ;

			if(ds_list.GetColumn(i, "TYPE1")=="1" )//착공
			{
				ds_list_tmp.AddRow();
			    ds_list_tmp.copyrow(cnt,ds_list,i);
				ds_list_tmp.SetColumn(cnt, "STATE", '1');
				ds_list_tmp.SetColumn(cnt, "APPLI", G_USER_ID);
				ds_list_tmp.SetColumn(cnt, "APPLD", gfn_getCurrDate());
				ds_list_tmp.SetColumn(cnt, "GJAHR", left(gfn_getCurrDate(), 4));
				ds_list_tmp.SetColumn(cnt, "SEQNO", seqno);

				//착공승인신청의 경우 자동승인 여부 파악(둘다 부과세 미포함으로)
				//if( ds_list.GetColumn(i, "DMBT3") >= (ds_list.GetColumn(i, "FAKWR") * ds_list.GetColumn(i, "RATE")) )
				if( ds_list.GetColumn(i, "DMBT3") >= ds_list.GetColumn(i, "FAKWR")  )
				{
					ds_list_tmp.SetColumn(cnt, "APPRS", gfn_getCurrDate());
					ds_list_tmp.SetColumn(cnt, "APPRL", 'AUTO');
					ds_list_tmp.SetColumn(cnt, "APPLS", gfn_getCurrDate());
					ds_list_tmp.SetColumn(cnt, "APPLL", 'AUTO');
					ds_list_tmp.SetColumn(cnt, "STATE", '3');
					ds_list_tmp.SetColumn(cnt, "OPEYNA", 'Y');
					ds_list_tmp.SetColumn(cnt, "OPEYNA2", 'Y');
					ds_list_tmp.SetColumn(cnt, "OPEYN", 'Y');
					ds_list_tmp.SetColumn(cnt, "OPELI", 'AUTO');
					ds_list_tmp.SetColumn(cnt, "OPELS", gfn_getCurrDate());
				}			


			}
			else if(ds_list.GetColumn(i, "TYPE1")=="2") //준공
			{
				ds_list_tmp.AddRow();
			    ds_list_tmp.copyrow(cnt, ds_list,i);
				ds_list_tmp.SetColumn(cnt, "STATE", '1');
				ds_list_tmp.SetColumn(cnt, "APPLI", G_USER_ID);
				ds_list_tmp.SetColumn(cnt, "APPLD", gfn_getCurrDate());
				ds_list_tmp.SetColumn(cnt, "GJAHR", left(gfn_getCurrDate(), 4));
				ds_list_tmp.SetColumn(cnt, "SEQNO", seqno);


			}

		    /*
			tit_clearActionInfo();
			tit_addSingleActionInfo("ps07:PS0708001A_I1");
			tit_callService(
				""
				, ""
				, "ds_list_tmp=ds_list_tmp"
				, ""
				, ""
				, ""//"fn_afterSave"
				, ""
				, ""
				, true);

			ds_list_tmp.ClearData();
			*/


			cnt = cnt + 1;
		}


	}


//trace(ds_list_tmp.SaveXML());			
//alert("xxxxx");		

	tit_clearActionInfo();
	//tit_addSingleActionInfo("ps07:PS0708001A_I1");
	tit_addMultiActionInfo("ps07:PS0708001A_I1");
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);

	ds_list_tmp.ClearData();		


}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}




	for(var i=0; i<ds_list.GetRowCount(); i++)
	{

		if( (ds_list.GetColumn(i, "STATE") == '3' || ds_list.GetColumn(i, "STATE") == '8') &&
		     ds_list.GetColumn(i, "CHK") == '1' )
		{
			var arr = [parseInt(i) + 1, "승인검토 또"];			
			gfn_showAlert("CW00132", arr);
			return;
		} 

	}	


    ds_list.Filter("CHK==1");
    ds_list_tmp.ClearData();
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();	


	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0708001A_D1");
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterDelete" 
		, ""
		, ""
		, true);

		ds_list_tmp.ClearData();

}


/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		fn_query();
	}
}
