function chk_excpcancel_OnClick(obj,strValue)
{
	ufn_setFilterForList();
	ds_list.row = -1;	

	gfn_showMsg("CI00002", ds_list.rowCount() + "");	
}


function ufn_setFilterForList() {
	var strFilter = "";	
	if(chk_excpcancel.Value == true)	strFilter += "TXT04 != 'C'&&";

	if(Right(strFilter,2) == "&&") 		strFilter = Left(strFilter,Length(strFilter)-2);
	ds_list.filter(strFilter);
}

/********************************************************************************
* 기      능   : 조회구분 변경시 
********************************************************************************/
function cb_type2_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(nNewIndex == 0) // 신청
	{
		btn_delete.enable = false;
		btn_request.enable = true;
	}	
	else if(nNewIndex == 1) //현황
	{
		btn_delete.enable = true;
		btn_request.enable = false;
	}
}
